INSERT INTO terms (id, name, tier, category, description) VALUES (1, 'ホモ＝サピエンス', 1, '先史時代', '現生人類。約20万年前にアフリカで誕生し全世界に拡散した');
INSERT INTO terms (id, name, tier, category, description) VALUES (2, '農耕・牧畜', 1, '先史時代', '約1万年前に始まった食料生産。定住生活と人口増加をもたらした');
INSERT INTO terms (id, name, tier, category, description) VALUES (3, '新石器革命', 1, '先史時代', '狩猟採集から農耕牧畜への転換。農業革命とも呼ばれる人類史最大の革命の一つ');
INSERT INTO terms (id, name, tier, category, description) VALUES (4, '文明', 1, '先史時代', '都市・文字・国家を持つ高度な人類社会。農業革命後に成立');
INSERT INTO terms (id, name, tier, category, description) VALUES (5, '文字', 1, '先史時代', '情報を記録・伝達する体系。文明の成立とともに発明された');
INSERT INTO terms (id, name, tier, category, description) VALUES (6, 'メソポタミア文明', 1, '古代オリエント', 'ティグリス・ユーフラテス川流域の古代文明。都市国家が発展');
INSERT INTO terms (id, name, tier, category, description) VALUES (7, 'ハンムラビ法典', 1, '古代オリエント', '古バビロニア王国の成文法。「目には目を」の報復法が特徴');
INSERT INTO terms (id, name, tier, category, description) VALUES (8, '鉄器', 1, '古代オリエント', '前1500年頃から普及した金属器。農業と軍事を革新した');
INSERT INTO terms (id, name, tier, category, description) VALUES (9, '都市国家', 2, '古代オリエント', '一つの都市とその周辺を統治する政治組織。古代の基本形態');
INSERT INTO terms (id, name, tier, category, description) VALUES (10, '楔形文字', 1, '古代オリエント', 'メソポタミアで発明された文字体系。粘土板に刻まれた');
INSERT INTO terms (id, name, tier, category, description) VALUES (11, 'エジプト文明', 1, '古代オリエント', 'ナイル川流域の古代文明。統一王朝が約3000年続いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (12, 'ファラオ', 1, '古代オリエント', '古代エジプトの王。神の化身として絶対的権力を持った');
INSERT INTO terms (id, name, tier, category, description) VALUES (13, 'ピラミッド', 1, '古代オリエント', 'エジプト王の巨大な墓。古王国時代に建造された');
INSERT INTO terms (id, name, tier, category, description) VALUES (14, 'ユダヤ教', 1, '古代オリエント', '唯一神ヤハウェを信仰する宗教。一神教の起源となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (15, '一神教', 1, '古代オリエント', '唯一の神のみを信仰する宗教形態。世界宗教の多くが該当');
INSERT INTO terms (id, name, tier, category, description) VALUES (16, '旧約聖書', 1, '古代オリエント', 'ユダヤ教とキリスト教の聖典。古代イスラエルの歴史を記す');
INSERT INTO terms (id, name, tier, category, description) VALUES (17, 'アケメネス朝ペルシア帝国', 2, '古代ペルシア', '前6世紀に成立した大帝国。オリエント全域を統一した');
INSERT INTO terms (id, name, tier, category, description) VALUES (18, 'ダレイオス１世', 2, '古代ペルシア', 'アケメネス朝最盛期の王。州制度と王の道を整備した');
INSERT INTO terms (id, name, tier, category, description) VALUES (19, '貨幣', 2, '古代オリエント', '小アジアのリディアで発明された交換手段。商業を発展させた');
INSERT INTO terms (id, name, tier, category, description) VALUES (20, 'フェニキア人', 2, '古代オリエント', '地中海東岸の商業民族。アルファベットの原型を発明した');
INSERT INTO terms (id, name, tier, category, description) VALUES (21, 'ペルセポリス', 2, '古代ペルシア', 'アケメネス朝の儀礼的首都。ダレイオス１世が建設した');
INSERT INTO terms (id, name, tier, category, description) VALUES (22, 'アレクサンドロス大王', 1, '古代ギリシア', 'マケドニア王。東方遠征でペルシアを征服しヘレニズム文化を生んだ');
INSERT INTO terms (id, name, tier, category, description) VALUES (23, 'ポリス', 1, '古代ギリシア', '古代ギリシアの都市国家。市民による政治参加が行われた');
INSERT INTO terms (id, name, tier, category, description) VALUES (24, 'アルファベット', 2, '古代ギリシア', 'フェニキア文字に起源を持つ表音文字体系。西洋文字の基礎');
INSERT INTO terms (id, name, tier, category, description) VALUES (25, 'アテネ', 1, '古代ギリシア', '古代ギリシアの都市国家。民主政と学問芸術の中心地');
INSERT INTO terms (id, name, tier, category, description) VALUES (26, 'スパルタ', 1, '古代ギリシア', '古代ギリシアの都市国家。厳格な軍事教育で知られた');
INSERT INTO terms (id, name, tier, category, description) VALUES (27, '奴隷制度', 1, '古代ギリシア', '人を財産として扱う制度。古代から近代まで広く存在し経済を支えた');
INSERT INTO terms (id, name, tier, category, description) VALUES (28, 'ササン朝ペルシア', 2, '古代ペルシア', '3世紀から7世紀のイラン王朝。ローマ・ビザンツと対峙した');
INSERT INTO terms (id, name, tier, category, description) VALUES (29, 'ミケーネ文明', 2, '古代ギリシア', '前1600年頃から栄えたギリシア最古の文明。線文字Bを使用');
INSERT INTO terms (id, name, tier, category, description) VALUES (30, 'パルティア', 2, '古代ペルシア', '前3世紀から後3世紀のイラン系王朝。シルクロード貿易で繁栄した');
INSERT INTO terms (id, name, tier, category, description) VALUES (31, 'ペルシア戦争', 1, '古代ギリシア', '前5世紀のギリシア諸都市とペルシア帝国の戦争。西洋文明を守った');
INSERT INTO terms (id, name, tier, category, description) VALUES (32, '民主政', 1, '古代ギリシア', '古代アテネで発展した市民による直接民主制。デモクラシーの起源となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (33, 'ペリクレス', 1, '古代ギリシア', '前5世紀アテネの政治家。民主政を完成させ黄金時代を築いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (34, 'ソクラテス', 1, '古代ギリシア', '前5世紀の哲学者。「無知の知」を説き対話によって真理を探究した');
INSERT INTO terms (id, name, tier, category, description) VALUES (35, 'プラトン', 1, '古代ギリシア', 'ソクラテスの弟子。イデア論を唱え西洋哲学の基礎を築いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (36, 'アリストテレス', 1, '古代ギリシア', 'プラトンの弟子。論理学・自然学を体系化し万学の祖と呼ばれる');
INSERT INTO terms (id, name, tier, category, description) VALUES (37, 'マケドニア', 2, '古代ギリシア', 'ギリシア北方の王国。アレクサンドロス大王の祖国として台頭した');
INSERT INTO terms (id, name, tier, category, description) VALUES (38, 'ペロポネソス戦争', 3, '古代ギリシア', '前5世紀のアテネ対スパルタの戦争。ギリシア世界を二分し疲弊させた');
INSERT INTO terms (id, name, tier, category, description) VALUES (39, 'ホメロス', 2, '古代ギリシア', '前8世紀頃の詩人。イリアスとオデュッセイアで西洋文学の祖とされる');
INSERT INTO terms (id, name, tier, category, description) VALUES (40, 'ヘロドトス', 3, '古代ギリシア', '前5世紀の歴史家。「歴史の父」と呼ばれペルシア戦争を記録した');
INSERT INTO terms (id, name, tier, category, description) VALUES (41, 'マラトンの戦い', 1, '古代ギリシア', '前490年のペルシア戦争でアテネが勝利。マラソン競技の語源となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (42, 'サラミスの海戦', 1, '古代ギリシア', '前480年のペルシア戦争でギリシア艦隊が勝利。ギリシアの独立を守った');
INSERT INTO terms (id, name, tier, category, description) VALUES (43, 'パルテノン', 1, '古代ギリシア', 'アテネのアクロポリスに建つ古代ギリシア最高傑作の神殿建築');
INSERT INTO terms (id, name, tier, category, description) VALUES (44, 'ヘレニズム', 1, '古代ギリシア', 'アレクサンドロス東方遠征後のギリシア文化と東方文化の融合時代');
INSERT INTO terms (id, name, tier, category, description) VALUES (45, 'ローマ', 1, '古代ローマ', '前8世紀建国から5世紀まで続いた古代最大の帝国。西洋文明の基盤');
INSERT INTO terms (id, name, tier, category, description) VALUES (46, '元老院', 1, '古代ローマ', 'ローマ共和政の最高議決機関。貴族によって構成され政策を決定した');
INSERT INTO terms (id, name, tier, category, description) VALUES (47, 'ローマ共和政', 1, '古代ローマ', '前509年から前27年まで続いた共和制。市民による統治が行われた');
INSERT INTO terms (id, name, tier, category, description) VALUES (48, 'ポエニ戦争', 1, '古代ローマ', '前3-2世紀のローマ対カルタゴの戦争。地中海覇権をかけて三度戦った');
INSERT INTO terms (id, name, tier, category, description) VALUES (49, 'ハンニバル', 1, '古代ローマ', 'カルタゴの名将。アルプス越えでイタリア侵攻しローマを脅かした');
INSERT INTO terms (id, name, tier, category, description) VALUES (50, 'カエサル', 1, '古代ローマ', 'ローマの軍人政治家。独裁官として共和政を事実上終わらせた');
INSERT INTO terms (id, name, tier, category, description) VALUES (51, 'グラックス兄弟の改革', 2, '古代ローマ', '前2世紀の土地改革の試み。元老院の抵抗で失敗し内乱の発端となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (52, '第１回三頭政治', 2, '古代ローマ', 'カエサル・ポンペイウス・クラッススの密約。共和政の終焉を加速させた');
INSERT INTO terms (id, name, tier, category, description) VALUES (53, 'ローマ帝国', 1, '古代ローマ', '前1世紀から5世紀まで続いた地中海世界の大帝国');
INSERT INTO terms (id, name, tier, category, description) VALUES (54, '十二表法', 1, '古代ローマ', '古代ローマ最初の成文法。前5世紀に制定され平民の権利を保障した');
INSERT INTO terms (id, name, tier, category, description) VALUES (55, '護民官', 2, '古代ローマ', 'ローマ共和政で平民の権利を守る役職。元老院決議に拒否権を持った');
INSERT INTO terms (id, name, tier, category, description) VALUES (56, 'カルタゴ', 1, '古代ローマ', '北アフリカのフェニキア人植民都市。地中海貿易で繁栄しローマと対立');
INSERT INTO terms (id, name, tier, category, description) VALUES (57, 'クレオパトラ', 2, '古代ローマ', 'プトレマイオス朝エジプト最後の女王。カエサルやアントニウスと結んだ');
INSERT INTO terms (id, name, tier, category, description) VALUES (58, 'アクティウムの海戦', 1, '古代ローマ', '前31年オクタウィアヌスがアントニウスとクレオパトラを破った海戦');
INSERT INTO terms (id, name, tier, category, description) VALUES (59, 'アウグストゥス', 1, '古代ローマ', 'ローマ初代皇帝。帝政の基礎を築き約200年の平和をもたらした');
INSERT INTO terms (id, name, tier, category, description) VALUES (60, 'パクス・ロマーナ', 1, '古代ローマ', 'ローマ帝国の平和。前1世紀から2世紀の約200年間の繁栄期');
INSERT INTO terms (id, name, tier, category, description) VALUES (61, 'キリスト教', 1, '古代ローマ', 'イエスを救世主とする一神教。ローマ帝国で国教化され世界宗教となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (62, 'イエス', 1, '古代ローマ', 'キリスト教の開祖。1世紀パレスチナで神の愛と救済を説いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (63, '新約聖書', 1, '古代ローマ', 'キリスト教の聖典。イエスと使徒たちの言行を記録した福音書など');
INSERT INTO terms (id, name, tier, category, description) VALUES (64, 'コンスタンティヌス', 1, '古代ローマ', 'キリスト教を公認したローマ皇帝。コンスタンティノープルを建設した');
INSERT INTO terms (id, name, tier, category, description) VALUES (65, 'コンスタンティノープル', 1, '古代ローマ', '東ローマ帝国の首都。ボスポラス海峡に面した東西交通の要衝');
INSERT INTO terms (id, name, tier, category, description) VALUES (66, 'ローマ法', 3, '古代ローマ', '古代ローマで発展した法体系。近代法の基礎となり継受された');
INSERT INTO terms (id, name, tier, category, description) VALUES (67, '東ローマ帝国', 2, '古代ローマ', '1453年まで続いたギリシア系キリスト教国家。ビザンツ帝国とも');
INSERT INTO terms (id, name, tier, category, description) VALUES (68, '西ローマ帝国の滅亡', 1, '古代ローマ', '476年の西ローマ帝国崩壊。古代の終わりとされる歴史的転換点');
INSERT INTO terms (id, name, tier, category, description) VALUES (69, '五賢帝', 1, '古代ローマ', '96-180年のローマ帝国最盛期を築いた5人の優れた皇帝たち');
INSERT INTO terms (id, name, tier, category, description) VALUES (70, 'トラヤヌス', 2, '古代ローマ', '五賢帝の一人。帝国最大版図を実現し善政で知られた');
INSERT INTO terms (id, name, tier, category, description) VALUES (71, 'ミラノ勅令', 2, '古代ローマ', '313年コンスタンティヌス帝によるキリスト教公認の勅令');
INSERT INTO terms (id, name, tier, category, description) VALUES (72, 'コロッセウム', 1, '古代ローマ', 'ローマの巨大円形闘技場。剣闘士の戦いなどが行われた');
INSERT INTO terms (id, name, tier, category, description) VALUES (73, 'アウグスティヌス', 3, '古代ローマ', '4-5世紀のキリスト教最大の教父。『神の国』で教義を体系化した');
INSERT INTO terms (id, name, tier, category, description) VALUES (74, 'インダス文明', 1, '古代インド', '前2500年頃の南アジア最古の文明。モヘンジョ・ダロなどの都市遺跡');
INSERT INTO terms (id, name, tier, category, description) VALUES (75, 'カースト制度', 2, '古代インド', 'インドの世襲的身分制度。バラモン教に起源を持ち現代まで影響を与える');
INSERT INTO terms (id, name, tier, category, description) VALUES (76, '仏教', 1, '古代インド', '釈迦を開祖とする宗教。インドで成立し東アジアに伝播した世界宗教');
INSERT INTO terms (id, name, tier, category, description) VALUES (77, '仏陀', 1, '古代インド', '仏教の開祖。悟りを開いたガウタマ・シッダールタの尊称で覚者を意味する');
INSERT INTO terms (id, name, tier, category, description) VALUES (78, 'アショーカ王', 3, '古代インド', '前3世紀マウリヤ朝の王。仏教を保護し理想的な仁政を実現した');
INSERT INTO terms (id, name, tier, category, description) VALUES (79, '大乗仏教', 2, '古代インド', '民衆救済を重視する仏教の一派。1世紀頃クシャーナ朝期に成立した');
INSERT INTO terms (id, name, tier, category, description) VALUES (80, 'ヒンドゥー教', 1, '古代インド', 'インド固有の多神教。カースト制度と結びつき現代まで影響を与える');
INSERT INTO terms (id, name, tier, category, description) VALUES (81, 'グプタ朝', 2, '古代インド', '4-6世紀のインド王朝。古典文化が開花し学問芸術が発展した');
INSERT INTO terms (id, name, tier, category, description) VALUES (82, 'ゼロの概念', 2, '古代インド', 'インドで発明された数学概念。数学と科学に革命をもたらした');
INSERT INTO terms (id, name, tier, category, description) VALUES (83, 'アンコール＝ワット', 2, '東南アジア', 'カンボジアの巨大ヒンドゥー寺院。12世紀建造で世界遺産');
INSERT INTO terms (id, name, tier, category, description) VALUES (84, '海の道', 3, '東南アジア', 'インド洋から東南アジアへの海上交易路。香辛料などが取引された');
INSERT INTO terms (id, name, tier, category, description) VALUES (85, '黄河文明', 1, '古代中国', '中国黄河流域の古代文明。四大文明の一つで中国文明の発祥地');
INSERT INTO terms (id, name, tier, category, description) VALUES (86, '長江文明', 3, '古代中国', '中国長江流域の稲作文明。黄河文明と並ぶ中国文明の源流');
INSERT INTO terms (id, name, tier, category, description) VALUES (87, '漢字', 1, '古代中国', '中国で発明された表意文字。東アジア文化圏の基礎となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (88, '殷', 1, '古代中国', '前16-前11世紀の中国最古の王朝。甲骨文字を使用した');
INSERT INTO terms (id, name, tier, category, description) VALUES (89, '周（西周）', 1, '古代中国', '前11-前8世紀の中国王朝。封建制を確立し儒教の理想時代とされた');
INSERT INTO terms (id, name, tier, category, description) VALUES (90, '封建制', 2, '古代中国', '周の統治制度。諸侯に土地を分封し主従関係で結んだ');
INSERT INTO terms (id, name, tier, category, description) VALUES (91, '春秋時代', 1, '古代中国', '前8-前5世紀の中国動乱期。周が東遷し覇者が台頭した');
INSERT INTO terms (id, name, tier, category, description) VALUES (92, '戦国時代', 1, '古代中国', '前5-前3世紀の中国動乱期。七雄が争い諸子百家が活躍した');
INSERT INTO terms (id, name, tier, category, description) VALUES (93, '孔子', 1, '古代中国', '儒学の開祖。春秋時代の思想家で仁と礼を重視した');
INSERT INTO terms (id, name, tier, category, description) VALUES (94, '儒学・儒教', 1, '古代中国', '孔子を祖とする中国の思想体系。東アジアの支配的思想となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (95, '諸子百家', 1, '古代中国', '春秋戦国時代の思想家集団。儒家・道家・法家などが活躍した');
INSERT INTO terms (id, name, tier, category, description) VALUES (96, '老子', 2, '古代中国', '道家の創始者。無為自然を説き儒教と対照的な思想を展開した');
INSERT INTO terms (id, name, tier, category, description) VALUES (97, '法家', 2, '古代中国', '法による厳格な統治を主張した思想。秦が採用し統一を実現した');
INSERT INTO terms (id, name, tier, category, description) VALUES (98, '秦（戦国）', 3, '古代中国', '戦国七雄の一つ。法家思想で富国強兵を実現し中国統一へ');
INSERT INTO terms (id, name, tier, category, description) VALUES (99, '鉄製農具', 3, '古代中国', '戦国時代に普及した農具。農業生産力を飛躍的に向上させた');
INSERT INTO terms (id, name, tier, category, description) VALUES (100, '始皇帝', 1, '古代中国', '秦の初代皇帝。前221年中国統一し中央集権体制を確立した');
INSERT INTO terms (id, name, tier, category, description) VALUES (101, '皇帝', 1, '古代中国', '中国の最高支配者の称号。始皇帝が天子に代えて採用した');
INSERT INTO terms (id, name, tier, category, description) VALUES (102, '郡県制', 2, '古代中国', '秦が導入した中央集権的地方行政制度。封建制に代わる統治方式');
INSERT INTO terms (id, name, tier, category, description) VALUES (103, '万里の長城', 1, '古代中国', '秦が北方遊牧民防衛のため建設した長大な城壁。後代も拡張された');
INSERT INTO terms (id, name, tier, category, description) VALUES (104, '前漢', 1, '古代中国', '前2世紀から後1世紀の中国統一王朝。漢とも呼ばれ儒教を国教化した');
INSERT INTO terms (id, name, tier, category, description) VALUES (105, '武帝', 1, '古代中国', '前漢最盛期の皇帝。領土拡大と儒教の官学化を実現した');
INSERT INTO terms (id, name, tier, category, description) VALUES (106, '匈奴', 2, '古代中国', '北方の遊牧騎馬民族。前漢の最大の脅威として対立した');
INSERT INTO terms (id, name, tier, category, description) VALUES (107, '張騫', 3, '古代中国', '前漢の外交官。西域への使者としてシルクロードを開拓した');
INSERT INTO terms (id, name, tier, category, description) VALUES (108, '司馬遷', 2, '古代中国', '前漢の歴史家。『史記』を著し中国歴史学の祖となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (109, '史記', 2, '古代中国', '司馬遷による紀伝体の歴史書。中国正史の模範となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (110, '項羽', 1, '古代中国', '秦末の武将。楚の将軍として秦を滅ぼしたが劉邦に敗れた');
INSERT INTO terms (id, name, tier, category, description) VALUES (111, '劉邦', 1, '古代中国', '漢の建国者で初代皇帝。農民出身から天下を統一した');
INSERT INTO terms (id, name, tier, category, description) VALUES (112, 'マヤ文明', 3, 'アメリカ大陸', 'メソアメリカの古代文明。精密な暦と数学体系を発達させた');
INSERT INTO terms (id, name, tier, category, description) VALUES (113, 'アステカ文明', 3, 'アメリカ大陸', 'メキシコ中央高原の先住民文明。16世紀スペインに征服された');
INSERT INTO terms (id, name, tier, category, description) VALUES (114, 'アンデス文明', 3, 'アメリカ大陸', '南米アンデス山脈の古代文明群。インカ帝国へと発展した');
INSERT INTO terms (id, name, tier, category, description) VALUES (115, 'トウモロコシ', 3, 'アメリカ大陸', 'アメリカ大陸原産の主食作物。世界中に伝播し重要作物となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (116, 'ジャガイモ', 3, 'アメリカ大陸', 'アンデス原産の作物。ヨーロッパに伝わり食糧革命をもたらした');
INSERT INTO terms (id, name, tier, category, description) VALUES (117, 'インカ帝国', 3, 'アメリカ大陸', '15-16世紀の南米アンデス帝国。スペインに征服され滅亡した');
INSERT INTO terms (id, name, tier, category, description) VALUES (118, 'マチュピチュ', 3, 'アメリカ大陸', 'ペルーのインカ遺跡。標高2400mの空中都市で世界遺産');
INSERT INTO terms (id, name, tier, category, description) VALUES (119, '遊牧民', 3, '中央アジア', '家畜を連れて移動する生活様式。農耕民と並ぶ人類の基本形態');
INSERT INTO terms (id, name, tier, category, description) VALUES (120, 'シルクロード', 2, '中央アジア', '中国と地中海世界を結ぶ交易路。絹が主要商品で文化交流も盛んだった');
INSERT INTO terms (id, name, tier, category, description) VALUES (121, '三国時代', 1, '古代中国', '3世紀の中国分裂時代。魏・呉・蜀の三国が鼎立した');
INSERT INTO terms (id, name, tier, category, description) VALUES (122, '曹操', 2, '古代中国', '三国時代の英雄。魏の基礎を築き『三国志』で知られる');
INSERT INTO terms (id, name, tier, category, description) VALUES (123, '諸葛孔明', 2, '古代中国', '蜀漢の軍師。知略の象徴として『三国志演義』で活躍する');
INSERT INTO terms (id, name, tier, category, description) VALUES (124, '北魏', 1, '古代中国', '5-6世紀の鮮卑族による北朝王朝。均田制を創始し漢化を進めた');
INSERT INTO terms (id, name, tier, category, description) VALUES (125, '均田制', 2, '古代中国', '農民に土地を分配する制度。北魏が創始し唐代に整備された');
INSERT INTO terms (id, name, tier, category, description) VALUES (126, '道教', 2, '古代中国', '中国三大宗教の一つ。老子の思想を基に民間信仰と融合して成立');
INSERT INTO terms (id, name, tier, category, description) VALUES (127, '高句麗', 3, '東アジア', '古代朝鮮半島北部から満州の国家。中国と対抗し独自の文化を築いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (128, '新羅', 3, '東アジア', '7世紀に朝鮮半島を統一した国家。唐と結び三国統一を達成した');
INSERT INTO terms (id, name, tier, category, description) VALUES (129, '百済', 3, '東アジア', '古代朝鮮半島南西部の国家。日本と交流が深く文化伝播の役割を果たした');
INSERT INTO terms (id, name, tier, category, description) VALUES (130, '隋', 1, '隋唐', '6世紀末の中国統一王朝。大運河建設と科挙制度を開始した');
INSERT INTO terms (id, name, tier, category, description) VALUES (131, '科挙', 1, '隋唐', '中国の官吏登用試験制度。隋唐時代に整備され科挙官僚を生んだ');
INSERT INTO terms (id, name, tier, category, description) VALUES (132, '大運河', 2, '隋唐', '隋が建設した南北を結ぶ大運河。経済統合と物資輸送に貢献した');
INSERT INTO terms (id, name, tier, category, description) VALUES (133, '唐', 1, '隋唐', '7-10世紀の中国統一王朝。国際的で東アジア文化圏の中心となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (134, '玄奘', 3, '隋唐', '唐の僧。インドに仏典を求めて17年の旅をした三蔵法師');
INSERT INTO terms (id, name, tier, category, description) VALUES (135, '禅宗', 3, '隋唐', '坐禅による悟りを重視する仏教宗派。中国から東アジアに広がった');
INSERT INTO terms (id, name, tier, category, description) VALUES (136, '李白', 2, '隋唐', '唐代最高の詩人。詩仙と称され自由奔放な作風で知られる');
INSERT INTO terms (id, name, tier, category, description) VALUES (137, '杜甫', 3, '隋唐', '唐代最高の詩人。詩聖と称され社会派の作風で知られる');
INSERT INTO terms (id, name, tier, category, description) VALUES (138, '突厥', 3, '隋唐', '6-8世紀の中央アジアのトルコ系遊牧帝国。最初のトルコ系国家');
INSERT INTO terms (id, name, tier, category, description) VALUES (139, 'ウイグル', 3, '隋唐', '8-9世紀の中央アジアのトルコ系遊牧帝国。突厥に代わって台頭した');
INSERT INTO terms (id, name, tier, category, description) VALUES (140, '遣唐使', 3, '隋唐', '日本が唐に派遣した使節。文化吸収が目的で遣隋使の後継');
INSERT INTO terms (id, name, tier, category, description) VALUES (141, '玄宗', 3, '隋唐', '唐の第6代皇帝。開元の治で唐の最盛期を築いたが後に混乱を招いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (142, 'ムハンマド', 1, 'イスラーム世界', 'イスラーム教の開祖。7世紀アラビアで唯一神アッラーの啓示を受けた預言者');
INSERT INTO terms (id, name, tier, category, description) VALUES (143, 'イスラーム教', 1, 'イスラーム世界', 'ムハンマドを預言者とする一神教。7世紀アラビアで成立し世界宗教となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (144, 'コーラン', 1, 'イスラーム世界', 'イスラーム教の聖典。ムハンマドへの啓示を記録しクルアーンとも');
INSERT INTO terms (id, name, tier, category, description) VALUES (145, 'ヒジュラ', 1, 'イスラーム世界', '622年のムハンマドのメッカからメディナへの移住。イスラーム暦の起点');
INSERT INTO terms (id, name, tier, category, description) VALUES (146, 'カリフ', 1, 'イスラーム世界', 'イスラーム共同体の最高指導者。ムハンマドの後継者');
INSERT INTO terms (id, name, tier, category, description) VALUES (147, 'ウマイヤ朝', 1, 'イスラーム世界', '7-8世紀の最初のイスラーム世襲王朝。ダマスクスを首都とし急拡大した');
INSERT INTO terms (id, name, tier, category, description) VALUES (148, 'スンナ派', 1, 'イスラーム世界', 'イスラーム教の最大宗派。正統カリフの伝統を重視し多数派を占める');
INSERT INTO terms (id, name, tier, category, description) VALUES (149, 'シーア派', 1, 'イスラーム世界', 'イスラーム教の一派。アリーとその子孫の正統性を主張する少数派');
INSERT INTO terms (id, name, tier, category, description) VALUES (150, 'アッバース朝', 1, 'イスラーム世界', '8-13世紀のイスラーム王朝。バグダードを首都とし学問が栄えた');
INSERT INTO terms (id, name, tier, category, description) VALUES (151, 'バグダード', 1, 'イスラーム世界', 'アッバース朝の首都。8世紀以降イスラーム文化の中心地として繁栄した');
INSERT INTO terms (id, name, tier, category, description) VALUES (152, 'ジハード', 2, 'イスラーム世界', 'イスラームにおける聖戦の概念。信仰を守るための努力を意味する');
INSERT INTO terms (id, name, tier, category, description) VALUES (153, 'セルジューク朝', 2, 'イスラーム世界', '11-13世紀のトルコ系イスラーム王朝。中央アジアから西アジアを支配');
INSERT INTO terms (id, name, tier, category, description) VALUES (154, 'スルタン', 2, 'イスラーム世界', 'イスラーム世界の君主の称号。世俗的権力者を指す');
INSERT INTO terms (id, name, tier, category, description) VALUES (155, 'マンジケルトの戦い', 3, 'イスラーム世界', '1071年のセルジューク朝対ビザンツ帝国の戦い。ビザンツに大打撃');
INSERT INTO terms (id, name, tier, category, description) VALUES (156, 'マムルーク', 3, 'イスラーム世界', 'トルコ系などの奴隷出身軍人。エジプトで王朝を建てた');
INSERT INTO terms (id, name, tier, category, description) VALUES (157, 'サラディン', 2, 'イスラーム世界', '12世紀のイスラーム側の英雄。十字軍と戦いエルサレムを奪回した');
INSERT INTO terms (id, name, tier, category, description) VALUES (158, 'マムルーク朝', 2, 'イスラーム世界', '13-16世紀のエジプト・シリアの軍人政権。マムルーク出身者が支配');
INSERT INTO terms (id, name, tier, category, description) VALUES (159, 'デリー＝スルタン朝', 3, '古代インド', '13-16世紀の北インドのイスラーム政権群の総称。デリーを拠点とした');
INSERT INTO terms (id, name, tier, category, description) VALUES (160, 'ガーナ王国', 3, 'アフリカ', '8-13世紀の西アフリカ王国。サハラ交易で繁栄し金の産地として有名');
INSERT INTO terms (id, name, tier, category, description) VALUES (161, 'マリ王国', 3, 'アフリカ', '13-16世紀の西アフリカ王国。イスラーム化し学問都市トンブクトゥを擁した');
INSERT INTO terms (id, name, tier, category, description) VALUES (162, '金と塩の交易', 3, 'アフリカ', 'サハラ砂漠を越える交易。金と塩の交換が中心で西アフリカを繁栄させた');
INSERT INTO terms (id, name, tier, category, description) VALUES (163, 'イスラーム文明', 1, 'イスラーム世界', 'イスラーム世界で発展した学問芸術の総合的文化体系。ギリシア文化を継承発展');
INSERT INTO terms (id, name, tier, category, description) VALUES (164, 'ゲルマン人の大移動', 1, '中世ヨーロッパ', '4-6世紀のゲルマン諸族のヨーロッパ移動。西ローマ帝国滅亡の要因');
INSERT INTO terms (id, name, tier, category, description) VALUES (165, 'フランク王国', 1, '中世ヨーロッパ', '5-9世紀のゲルマン系国家。8世紀カール大帝が西ヨーロッパを統一した');
INSERT INTO terms (id, name, tier, category, description) VALUES (166, 'クローヴィス', 3, '中世ヨーロッパ', 'フランク王国メロヴィング朝の建国者。496年カトリックに改宗した');
INSERT INTO terms (id, name, tier, category, description) VALUES (167, 'カール・マルテル', 3, '中世ヨーロッパ', 'カロリング朝の宮宰。732年トゥール・ポワティエでイスラーム軍を撃退');
INSERT INTO terms (id, name, tier, category, description) VALUES (168, 'カロリング朝', 2, '中世ヨーロッパ', 'フランク王国の第二王朝。カール大帝の一族が支配した');
INSERT INTO terms (id, name, tier, category, description) VALUES (169, 'ローマ教皇', 1, '中世ヨーロッパ', 'ローマ＝カトリック教会の最高指導者。中世西ヨーロッパで強大な権力');
INSERT INTO terms (id, name, tier, category, description) VALUES (170, '千夜一夜物語', 3, 'イスラーム世界', 'アラビアンナイト。アッバース朝期を舞台としたアラビア語の物語集');
INSERT INTO terms (id, name, tier, category, description) VALUES (171, 'カール大帝', 2, '中世ヨーロッパ', 'フランク王国カロリング朝の王。800年西ローマ帝国復興を宣言された');
INSERT INTO terms (id, name, tier, category, description) VALUES (172, '神聖ローマ帝国', 1, '中世ヨーロッパ', '962年成立の中欧の帝国。神聖でもローマ的でも帝国でもないと評された');
INSERT INTO terms (id, name, tier, category, description) VALUES (173, 'ノルマン人', 2, '中世ヨーロッパ', '8-11世紀の北方ゲルマン系民族。ヴァイキングとも呼ばれ各地を侵攻');
INSERT INTO terms (id, name, tier, category, description) VALUES (174, 'ノルマン・コンクェスト', 3, '中世ヨーロッパ', '1066年のノルマン人によるイングランド征服。ノルマン朝成立');
INSERT INTO terms (id, name, tier, category, description) VALUES (175, '封建社会', 1, '中世ヨーロッパ', '土地を媒介とした主従関係に基づく中世ヨーロッパの社会体制');
INSERT INTO terms (id, name, tier, category, description) VALUES (176, '農奴制', 1, '中世ヨーロッパ', '中世ヨーロッパの農民身分制度。土地に緊縛され領主に隷属した');
INSERT INTO terms (id, name, tier, category, description) VALUES (177, '叙任権闘争', 1, '中世ヨーロッパ', '11-12世紀の教皇と皇帝の聖職者任命権をめぐる対立');
INSERT INTO terms (id, name, tier, category, description) VALUES (178, 'インノケンティウス3世', 3, '中世ヨーロッパ', '13世紀の教皇。教皇権の最盛期を築き世俗権力を圧倒した');
INSERT INTO terms (id, name, tier, category, description) VALUES (179, 'ビザンツ帝国', 1, 'ビザンツ・東欧', '東ローマ帝国。395年分裂から1453年滅亡まで1000年続いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (180, 'ユスティニアヌス', 3, 'ビザンツ・東欧', 'ビザンツ帝国最盛期の皇帝。ローマ法大全を編纂し領土回復を図った');
INSERT INTO terms (id, name, tier, category, description) VALUES (181, 'ローマ法大全', 2, 'ビザンツ・東欧', 'ユスティニアヌス帝が編纂したローマ法の集大成。近代法に影響');
INSERT INTO terms (id, name, tier, category, description) VALUES (182, 'スラヴ人', 2, 'ビザンツ・東欧', '東ヨーロッパの主要民族集団。ロシア・ポーランドなど多数の国家を形成');
INSERT INTO terms (id, name, tier, category, description) VALUES (183, '十字軍運動', 1, '中世ヨーロッパ', '11-13世紀の聖地回復を目指したキリスト教徒の遠征。中世最大の宗教戦争');
INSERT INTO terms (id, name, tier, category, description) VALUES (184, '東方貿易', 2, '中世ヨーロッパ', '十字軍後に発展した地中海東方との商業貿易。イタリア都市が主導');
INSERT INTO terms (id, name, tier, category, description) VALUES (185, 'ヴェネツィア', 2, '中世ヨーロッパ', '地中海商業の中心都市。十字軍で栄え東方貿易を独占した');
INSERT INTO terms (id, name, tier, category, description) VALUES (186, 'モスクワ大公国', 2, 'ビザンツ・東欧', '14世紀以降のロシア統一の中心。モンゴル支配から独立しロシア帝国へ');
INSERT INTO terms (id, name, tier, category, description) VALUES (187, 'ポーランド王国', 2, 'ビザンツ・東欧', '10-18世紀の東欧の大国。リトアニアと合同し広大な領土を持った');
INSERT INTO terms (id, name, tier, category, description) VALUES (188, 'ハンザ同盟', 2, '中世ヨーロッパ', '13-17世紀の北ヨーロッパ都市の商業同盟。バルト海交易を支配した');
INSERT INTO terms (id, name, tier, category, description) VALUES (189, 'ギルド', 2, '中世ヨーロッパ', '中世都市の同業組合。職人や商人が組織し生産流通を統制した');
INSERT INTO terms (id, name, tier, category, description) VALUES (190, '黒死病', 2, '中世ヨーロッパ', '14世紀のペスト大流行。ヨーロッパ人口の3分の1が死亡し社会変動');
INSERT INTO terms (id, name, tier, category, description) VALUES (191, '教会大分裂', 3, '中世ヨーロッパ', '14-15世紀の教会大分裂。複数の教皇が並立し教皇権威が失墜した');
INSERT INTO terms (id, name, tier, category, description) VALUES (192, '百年戦争', 1, '中世ヨーロッパ', '1337-1453年の英仏間の長期戦争。王位継承と領土をめぐり争った');
INSERT INTO terms (id, name, tier, category, description) VALUES (193, 'ジャンヌ・ダルク', 3, '中世ヨーロッパ', '百年戦争のフランス側英雄。オルレアンを解放したが異端として処刑された');
INSERT INTO terms (id, name, tier, category, description) VALUES (194, 'マグナ・カルタ', 1, '中世ヨーロッパ', '1215年の大憲章。イギリス王ジョンが貴族に認めた王権制限文書');
INSERT INTO terms (id, name, tier, category, description) VALUES (195, '議会制度', 2, '中世ヨーロッパ', 'イギリスで発展した議会による統治制度。近代民主政の基礎となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (196, 'フィリップ4世', 3, '中世ヨーロッパ', '13-14世紀のフランス王。教皇と対立し王権を強化した');
INSERT INTO terms (id, name, tier, category, description) VALUES (197, 'レコンキスタ', 3, '中世ヨーロッパ', '8-15世紀のイベリア半島でのキリスト教徒による国土回復運動');
INSERT INTO terms (id, name, tier, category, description) VALUES (198, 'スペイン王国', 2, '中世ヨーロッパ', '1479年カスティリャとアラゴンの統一で成立。レコンキスタを完成');
INSERT INTO terms (id, name, tier, category, description) VALUES (199, 'ハプスブルク家', 2, '中世ヨーロッパ', '13-20世紀の欧州最大の王家。神聖ローマ皇帝やスペイン王を輩出');
INSERT INTO terms (id, name, tier, category, description) VALUES (200, '金印勅書', 3, '中世ヨーロッパ', '1356年の神聖ローマ帝国の基本法。皇帝選挙制度を確立した');
INSERT INTO terms (id, name, tier, category, description) VALUES (201, 'スコラ学', 3, '中世ヨーロッパ', '中世キリスト教神学と哲学の融合。信仰と理性の調和を目指した');
INSERT INTO terms (id, name, tier, category, description) VALUES (202, 'トマス・アクィナス', 3, '中世ヨーロッパ', '13世紀のスコラ哲学者。神学大全を著しアリストテレスと融合させた');
INSERT INTO terms (id, name, tier, category, description) VALUES (203, '大学', 2, '中世ヨーロッパ', '12世紀以降の中世ヨーロッパ高等教育機関。パリ・ボローニャなどが起源');
INSERT INTO terms (id, name, tier, category, description) VALUES (204, 'ゴシック様式', 3, '中世ヨーロッパ', '12-16世紀の建築様式。尖塔アーチと高い天井が特徴の大聖堂');
INSERT INTO terms (id, name, tier, category, description) VALUES (205, 'ソグド人', 3, '中央アジア', '中央アジアのイラン系商業民族。シルクロード交易の担い手となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (206, '宋', 1, '宋元', '10-13世紀の中国王朝。文治主義と商業経済が発展し文化が開花した');
INSERT INTO terms (id, name, tier, category, description) VALUES (207, '北宋', 2, '宋元', '960-1127年の宋前半期。開封を都とし契丹・西夏と対峙した');
INSERT INTO terms (id, name, tier, category, description) VALUES (208, '王安石', 3, '宋元', '11世紀の宋の改革者。新法で富国強兵図るも保守派の抵抗で挫折');
INSERT INTO terms (id, name, tier, category, description) VALUES (209, '南宋', 2, '宋元', '1127-1279年の宋後半期。金の侵攻で江南に遷都し臨安を都とした');
INSERT INTO terms (id, name, tier, category, description) VALUES (210, '交子', 2, '宋元', '11世紀宋で発行された世界初の紙幣。商業発展の象徴');
INSERT INTO terms (id, name, tier, category, description) VALUES (211, '遼', 1, '宋元', '10-12世紀の契丹族による征服王朝。華北と北アジアを支配した');
INSERT INTO terms (id, name, tier, category, description) VALUES (212, '金', 2, '宋元', '12-13世紀の女真族による征服王朝。宋を圧迫し華北を支配');
INSERT INTO terms (id, name, tier, category, description) VALUES (213, '朱子学', 3, '宋元', '朱熹が大成した儒学の一派。理気論が特徴で東アジアの正統学問となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (214, '朱熹', 3, '宋元', '12世紀の儒学者。朱子学を大成し儒教を哲学的に体系化した');
INSERT INTO terms (id, name, tier, category, description) VALUES (215, 'モンゴル帝国', 1, '宋元', '13世紀チンギス・ハンが建国した史上最大の陸上帝国');
INSERT INTO terms (id, name, tier, category, description) VALUES (216, 'チンギス＝ハン', 1, '宋元', 'モンゴル帝国の建国者。13世紀にユーラシア大陸の大半を征服した');
INSERT INTO terms (id, name, tier, category, description) VALUES (217, 'フビライ＝ハン', 1, '宋元', '元の初代皇帝。チンギス・ハンの孫で中国全土を征服し大都を建設');
INSERT INTO terms (id, name, tier, category, description) VALUES (218, '元', 1, '宋元', '13-14世紀のモンゴル系王朝。中国を支配し東西交流を促進した');
INSERT INTO terms (id, name, tier, category, description) VALUES (219, '朱元璋', 3, '明清', '明の建国者で初代皇帝。元を倒し漢民族王朝を復興させた洪武帝');
INSERT INTO terms (id, name, tier, category, description) VALUES (220, '明', 1, '明清', '14-17世紀の中国王朝。漢民族王朝として復興し海禁政策をとった');
INSERT INTO terms (id, name, tier, category, description) VALUES (221, '鄭和', 3, '明清', '明の宦官提督。15世紀に大航海で東南アジアからアフリカまで到達した');
INSERT INTO terms (id, name, tier, category, description) VALUES (222, '永楽帝', 3, '明清', '明の第3代皇帝。永楽帝として知られ鄭和の大航海を命じた');
INSERT INTO terms (id, name, tier, category, description) VALUES (223, '朝貢貿易', 2, '明清', '中国皇帝を中心とした東アジアの外交貿易体制。朝貢による交易');
INSERT INTO terms (id, name, tier, category, description) VALUES (224, '陽明学', 3, '明清', '王陽明が創始した儒学の一派。心即理を説き朱子学に対抗した');
INSERT INTO terms (id, name, tier, category, description) VALUES (225, 'マテオ＝リッチ', 3, '明清', '16-17世紀にイエズス会士として中国で布教。西洋科学も伝えた');
INSERT INTO terms (id, name, tier, category, description) VALUES (226, '清', 1, '明清', '17-20世紀の中国最後の王朝。満州族のヌルハチが建国した');
INSERT INTO terms (id, name, tier, category, description) VALUES (227, '康熙帝', 3, '明清', '清の第4代皇帝。在位61年で最盛期を築き領土を最大に拡大した');
INSERT INTO terms (id, name, tier, category, description) VALUES (228, '乾隆帝', 3, '明清', '清の第6代皇帝。在位60年で領土を最大に拡大し文化事業も推進した');
INSERT INTO terms (id, name, tier, category, description) VALUES (229, 'ティムール', 2, 'イスラーム世界', '14世紀の中央アジアの征服者。サマルカンドを都に大帝国を建設した');
INSERT INTO terms (id, name, tier, category, description) VALUES (230, 'ティムール朝', 2, 'イスラーム世界', '14-16世紀の中央アジア・イラン帝国。ティムールが建国しサマルカンドが栄えた');
INSERT INTO terms (id, name, tier, category, description) VALUES (231, 'オスマン帝国', 1, 'イスラーム世界', '14-20世紀のトルコ系大帝国。地中海東部からバルカンまで支配した');
INSERT INTO terms (id, name, tier, category, description) VALUES (232, 'メフメト2世', 2, 'イスラーム世界', 'オスマン帝国第7代スルタン。1453年コンスタンティノープルを陥落させた');
INSERT INTO terms (id, name, tier, category, description) VALUES (233, 'スレイマン1世', 2, 'イスラーム世界', 'オスマン帝国最盛期の第10代スルタン。大帝と呼ばれ領土を最大に拡大');
INSERT INTO terms (id, name, tier, category, description) VALUES (234, 'サファヴィー朝', 2, 'イスラーム世界', '16-18世紀のイラン・シーア派王朝。イスファハーンを都とした');
INSERT INTO terms (id, name, tier, category, description) VALUES (235, 'ムガル帝国', 2, 'イスラーム世界', '16-19世紀のインド・イスラーム王朝。ムガル様式文化が開花した');
INSERT INTO terms (id, name, tier, category, description) VALUES (236, 'アクバル', 2, 'イスラーム世界', 'ムガル帝国第3代皇帝。インド統一を達成し宗教融和政策を実施した');
INSERT INTO terms (id, name, tier, category, description) VALUES (237, 'コロンブス', 1, '大航海時代', 'イタリア出身の航海者。1492年スペイン王の支援でアメリカ大陸に到達');
INSERT INTO terms (id, name, tier, category, description) VALUES (238, 'ヴァスコ＝ダ＝ガマ', 1, '大航海時代', 'ポルトガルの航海者。1498年喜望峰経由でインドへの航路を開拓した');
INSERT INTO terms (id, name, tier, category, description) VALUES (239, 'マゼラン', 1, '大航海時代', 'ポルトガルの航海者。1519-22年艦隊が初めて世界周航を達成した');
INSERT INTO terms (id, name, tier, category, description) VALUES (240, 'コルテス', 2, '大航海時代', 'スペインの征服者コンキスタドール。1521年アステカ帝国を滅ぼした');
INSERT INTO terms (id, name, tier, category, description) VALUES (241, 'ピサロ', 2, '大航海時代', 'スペインの征服者コンキスタドール。1533年インカ帝国を滅ぼした');
INSERT INTO terms (id, name, tier, category, description) VALUES (242, 'オランダ東インド会社', 2, '大航海時代', '1602年設立の世界初の株式会社。アジア貿易を独占し近代資本主義の先駆');
INSERT INTO terms (id, name, tier, category, description) VALUES (243, 'イギリス東インド会社', 1, '大航海時代', '1600年設立のイギリス特許会社。インド進出の主体となり後の植民地化につながった');
INSERT INTO terms (id, name, tier, category, description) VALUES (244, 'トルデシリャス条約', 2, '大航海時代', '1494年のスペインとポルトガルの世界分割協定。教皇の仲裁で成立');
INSERT INTO terms (id, name, tier, category, description) VALUES (245, '商業革命', 3, '大航海時代', '大航海時代の世界的商業拡大。ヨーロッパ経済を変革し資本主義を促進した');
INSERT INTO terms (id, name, tier, category, description) VALUES (246, '価格革命', 3, '大航海時代', '16世紀のヨーロッパ物価上昇。アメリカ大陸からの大量の銀流入が原因');
INSERT INTO terms (id, name, tier, category, description) VALUES (247, 'ルネサンス', 1, 'ルネサンス・宗教改革', '14-16世紀の文化運動。古典古代の復興を目指しイタリアで始まった');
INSERT INTO terms (id, name, tier, category, description) VALUES (248, '宗教改革', 1, 'ルネサンス・宗教改革', '16世紀のキリスト教改革運動。ルターに始まりプロテスタント成立');
INSERT INTO terms (id, name, tier, category, description) VALUES (249, 'ルター', 1, 'ルネサンス・宗教改革', '宗教改革の先駆者。1517年95か条の論題で免罪符販売を批判した');
INSERT INTO terms (id, name, tier, category, description) VALUES (250, 'カルヴァン', 2, 'ルネサンス・宗教改革', '宗教改革者。予定説を唱えジュネーヴで神権政治を実施した');
INSERT INTO terms (id, name, tier, category, description) VALUES (251, 'レオナルド＝ダ＝ヴィンチ', 2, 'ルネサンス・宗教改革', 'ルネサンス期の万能人。モナリザなど芸術と科学の両面で活躍した');
INSERT INTO terms (id, name, tier, category, description) VALUES (252, 'ミケランジェロ', 2, 'ルネサンス・宗教改革', 'ルネサンス期の彫刻家画家。ダヴィデ像やシスティナ礼拝堂天井画が有名');
INSERT INTO terms (id, name, tier, category, description) VALUES (253, '九十五ヶ条の論題', 2, 'ルネサンス・宗教改革', '1517年ルターが発表した宗教改革の発端となる文書。免罪符を批判');
INSERT INTO terms (id, name, tier, category, description) VALUES (254, '予定説', 2, 'ルネサンス・宗教改革', 'カルヴァン派の核心教義。救済は神が予め定めているとする説');
INSERT INTO terms (id, name, tier, category, description) VALUES (255, 'マキァヴェリ', 2, 'ルネサンス・宗教改革', 'ルネサンス期の政治思想家。君主論で権力維持の現実主義を説いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (256, '活版印刷術', 1, 'ルネサンス・宗教改革', 'グーテンベルクが発明した印刷技術。知識の大衆化をもたらした');
INSERT INTO terms (id, name, tier, category, description) VALUES (257, 'コペルニクス', 1, 'ルネサンス・宗教改革', '16世紀の天文学者。地動説を唱え天体運行論で科学革命の先駆となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (258, 'グーテンベルク', 1, 'ルネサンス・宗教改革', '15世紀のドイツ人。活版印刷術を発明し聖書を大量印刷した');
INSERT INTO terms (id, name, tier, category, description) VALUES (259, 'アウクスブルクの和議', 2, 'ルネサンス・宗教改革', '1555年の神聖ローマ帝国の宗教和議。領主が領内の宗教を決定する権利');
INSERT INTO terms (id, name, tier, category, description) VALUES (260, '絶対王政', 1, '主権国家形成', '16-18世紀の中央集権的君主制。王権神授説を根拠に強大な権力を行使');
INSERT INTO terms (id, name, tier, category, description) VALUES (261, '主権国家', 1, '主権国家形成', '対外的主権と対内的統治権を持つ国家。ウェストファリア条約で確立');
INSERT INTO terms (id, name, tier, category, description) VALUES (262, 'イギリス国教会', 2, 'ルネサンス・宗教改革', 'ヘンリ8世がローマ教会から独立して成立。国王が首長となる');
INSERT INTO terms (id, name, tier, category, description) VALUES (263, 'プロテスタント', 2, 'ルネサンス・宗教改革', '宗教改革で成立したキリスト教諸派の総称。ローマ教会に抗議した');
INSERT INTO terms (id, name, tier, category, description) VALUES (264, 'フェリペ2世', 3, '主権国家形成', '16世紀のスペイン王。最大版図を築いたが無敵艦隊敗北で衰退始まる');
INSERT INTO terms (id, name, tier, category, description) VALUES (265, 'オランダ独立戦争', 2, '主権国家形成', '1568-1609年のオランダの対スペイン独立戦争。市民革命の先駆');
INSERT INTO terms (id, name, tier, category, description) VALUES (266, 'エリザベス1世', 2, '主権国家形成', '16世紀イギリス女王。エリザベス朝として繁栄しスペイン無敵艦隊を破った');
INSERT INTO terms (id, name, tier, category, description) VALUES (267, '無敵艦隊', 2, '主権国家形成', 'スペインの大艦隊アルマダ。1588年英国海峡でイギリスに敗北した');
INSERT INTO terms (id, name, tier, category, description) VALUES (268, 'マニュファクチュア', 2, '主権国家形成', '工場制手工業。産業革命前の分業による生産形態で資本主義萌芽');
INSERT INTO terms (id, name, tier, category, description) VALUES (269, '三十年戦争', 1, '主権国家形成', '1618-48年の宗教戦争。神聖ローマ帝国を舞台に諸国が参戦し荒廃');
INSERT INTO terms (id, name, tier, category, description) VALUES (270, 'ウェストファリア条約', 1, '主権国家形成', '1648年の三十年戦争講和条約。主権国家体制の基礎を確立した');
INSERT INTO terms (id, name, tier, category, description) VALUES (271, 'ルイ14世', 2, '主権国家形成', 'フランス絶対王政最盛期の王。太陽王と呼ばれヴェルサイユ宮殿を建設');
INSERT INTO terms (id, name, tier, category, description) VALUES (272, 'ピューリタン革命', 1, 'イギリス革命', '1640年代のイギリス革命。議会派が王党派を破りチャールズ1世を処刑');
INSERT INTO terms (id, name, tier, category, description) VALUES (273, 'クロムウェル', 2, 'イギリス革命', 'ピューリタン革命の指導者。王を処刑後護国卿として独裁政治を行った');
INSERT INTO terms (id, name, tier, category, description) VALUES (274, '名誉革命', 1, 'イギリス革命', '1688年のイギリス無血革命。ジェームズ2世を追放し立憲君主制確立');
INSERT INTO terms (id, name, tier, category, description) VALUES (275, '権利の章典', 1, 'イギリス革命', '1689年制定の権利の宣言。議会主権を確立し国王の権限を制限した');
INSERT INTO terms (id, name, tier, category, description) VALUES (276, '航海法', 2, '主権国家形成', '1651年制定のイギリス重商主義政策。英国船以外の貿易を制限');
INSERT INTO terms (id, name, tier, category, description) VALUES (277, 'ブルボン朝', 2, '主権国家形成', '16-18世紀のフランス王朝。アンリ4世に始まり絶対王政を確立');
INSERT INTO terms (id, name, tier, category, description) VALUES (278, '重商主義', 2, '主権国家形成', '絶対王政期の経済政策。貿易黒字を重視し国富の増大を図った');
INSERT INTO terms (id, name, tier, category, description) VALUES (279, '責任内閣制', 3, '18世紀ヨーロッパ', '議会に対して責任を負う内閣制度。議会民主主義の核心的制度');
INSERT INTO terms (id, name, tier, category, description) VALUES (280, '立憲君主政', 3, '18世紀ヨーロッパ', '憲法によって君主の権限を制限する政治体制。近代政治の一形態');
INSERT INTO terms (id, name, tier, category, description) VALUES (281, 'ピョートル1世', 3, '18世紀ヨーロッパ', 'ロシア皇帝。18世紀初頭に西欧化改革を断行し近代化を推進した');
INSERT INTO terms (id, name, tier, category, description) VALUES (282, 'エカチェリーナ2世', 3, '18世紀ヨーロッパ', '18世紀ロシア女帝。啓蒙専制君主として改革を進め領土を拡大した');
INSERT INTO terms (id, name, tier, category, description) VALUES (283, 'マリア＝テレジア', 3, '18世紀ヨーロッパ', '18世紀オーストリア女帝。啓蒙専制君主として改革を実施した');
INSERT INTO terms (id, name, tier, category, description) VALUES (284, '七年戦争', 3, '18世紀ヨーロッパ', '1756-63年の世界規模の戦争。英仏の植民地争奪が中心となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (285, '啓蒙専制主義', 3, '18世紀ヨーロッパ', '18世紀の改革的君主制。啓蒙思想を取り入れ上からの近代化を図った');
INSERT INTO terms (id, name, tier, category, description) VALUES (286, 'ポーランド分割', 3, '18世紀ヨーロッパ', '18世紀後半のポーランド領土分割。露墺普が三度にわたり分割した');
INSERT INTO terms (id, name, tier, category, description) VALUES (287, 'スペイン継承戦争', 3, '18世紀ヨーロッパ', '1701-14年のスペイン王位継承をめぐる戦争。ヨーロッパ勢力均衡確立');
INSERT INTO terms (id, name, tier, category, description) VALUES (288, '外交革命', 3, '18世紀ヨーロッパ', '1756年の同盟関係大転換。英墺対立から英普・仏墺同盟へ');
INSERT INTO terms (id, name, tier, category, description) VALUES (289, '三角貿易', 1, '植民地時代', '大西洋を結ぶ奴隷貿易。ヨーロッパ・アフリカ・アメリカを三角形に結んだ');
INSERT INTO terms (id, name, tier, category, description) VALUES (290, 'プランテーション', 2, '植民地時代', '新大陸や熱帯植民地の大規模農園。黒人奴隷労働に依存した');
INSERT INTO terms (id, name, tier, category, description) VALUES (291, '科学革命', 3, '18世紀ヨーロッパ', '16-17世紀の自然科学の発展。近代科学の成立をもたらした');
INSERT INTO terms (id, name, tier, category, description) VALUES (292, 'ニュートン', 1, '18世紀ヨーロッパ', 'イギリスの科学者。万有引力の法則を発見し近代科学の基礎を築いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (293, '啓蒙思想', 1, '18世紀ヨーロッパ', '18世紀の理性と進歩を重視する思想運動。フランスで展開された');
INSERT INTO terms (id, name, tier, category, description) VALUES (294, 'ロック', 1, '18世紀ヨーロッパ', 'イギリスの哲学者。社会契約説と抵抗権を主張し名誉革命を正当化');
INSERT INTO terms (id, name, tier, category, description) VALUES (295, 'ルソー', 1, '18世紀ヨーロッパ', 'フランスの思想家。人民主権と社会契約を説きフランス革命に影響');
INSERT INTO terms (id, name, tier, category, description) VALUES (296, 'モンテスキュー', 1, '18世紀ヨーロッパ', 'フランスの思想家。法の精神で三権分立論を唱え立憲主義を主張');
INSERT INTO terms (id, name, tier, category, description) VALUES (297, 'アダム＝スミス', 3, '18世紀ヨーロッパ', 'イギリスの経済学者。国富論で自由放任主義を唱え古典派経済学を創始');
INSERT INTO terms (id, name, tier, category, description) VALUES (298, 'デカルト', 2, '18世紀ヨーロッパ', 'フランスの哲学者数学者。我思う故に我ありで近代哲学の祖となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (299, 'ホッブズ', 2, '18世紀ヨーロッパ', 'イギリスの哲学者。リヴァイアサンで社会契約説の先駆となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (300, 'グロティウス', 3, '18世紀ヨーロッパ', 'オランダの法学者。戦争と平和の法で国際法の基礎を築いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (301, '社会契約説', 1, '18世紀ヨーロッパ', '国家の起源を人民の合意に求める思想。近代政治思想の基盤となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (302, '産業革命', 1, '産業革命', '18世紀後半イギリスで始まった工業化。社会を根本的に変革した');
INSERT INTO terms (id, name, tier, category, description) VALUES (303, '蒸気機関', 1, '産業革命', '産業革命の原動力となった動力機関。石炭で水を沸騰させ動力を得る');
INSERT INTO terms (id, name, tier, category, description) VALUES (304, 'ワット', 2, '産業革命', 'スコットランドの技術者。蒸気機関を改良し産業革命を加速させた');
INSERT INTO terms (id, name, tier, category, description) VALUES (305, '鉄道', 2, '産業革命', '蒸気機関車による輸送革命。19世紀の交通革命の中核となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (306, 'アメリカ独立戦争', 1, 'アメリカ独立革命', '1775-83年のイギリスからの独立戦争。近代民主主義の出発点');
INSERT INTO terms (id, name, tier, category, description) VALUES (307, 'アメリカ独立宣言', 1, 'アメリカ独立革命', '1776年の独立宣言。人権思想を表明し近代民主主義の基本文書となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (308, 'ワシントン', 1, 'アメリカ独立革命', 'アメリカ独立戦争の指導者で初代大統領。建国の父と呼ばれる');
INSERT INTO terms (id, name, tier, category, description) VALUES (309, 'アメリカ合衆国憲法', 1, 'アメリカ独立革命', '1787年制定のアメリカ憲法。三権分立を規定し近代成文憲法の模範');
INSERT INTO terms (id, name, tier, category, description) VALUES (310, '三権分立', 1, 'アメリカ独立革命', '立法・行政・司法の三権を分立させる制度。近代政治制度の基本原理');
INSERT INTO terms (id, name, tier, category, description) VALUES (311, '資本主義', 1, '産業革命', '私有財産と市場経済を基礎とする経済体制。産業革命後に確立');
INSERT INTO terms (id, name, tier, category, description) VALUES (312, 'フランス革命', 1, 'フランス革命', '1789年勃発の革命。絶対王政を打倒し自由・平等・博愛を掲げた');
INSERT INTO terms (id, name, tier, category, description) VALUES (313, 'アンシャン＝レジーム', 2, 'フランス革命', 'フランス革命前の旧体制。特権身分による不平等な社会体制');
INSERT INTO terms (id, name, tier, category, description) VALUES (314, 'バスティーユ牢獄襲撃', 1, 'フランス革命', '1789年7月14日のバスティーユ牢獄襲撃。フランス革命の象徴的開始');
INSERT INTO terms (id, name, tier, category, description) VALUES (315, '人権宣言', 1, 'フランス革命', 'フランス革命の理念を示した1789年の宣言。自由・平等を謳った');
INSERT INTO terms (id, name, tier, category, description) VALUES (316, '国民議会', 2, 'フランス革命', '1789年第三身分が結成した議会。フランス革命の主体となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (317, 'ジャコバン派', 2, 'フランス革命', 'フランス革命の急進派。1793-94年に恐怖政治を実施した');
INSERT INTO terms (id, name, tier, category, description) VALUES (318, 'ロベスピエール', 2, 'フランス革命', 'ジャコバン派の指導者。1793-94年に恐怖政治を主導したが処刑された');
INSERT INTO terms (id, name, tier, category, description) VALUES (319, '第一共和政', 1, 'フランス革命', '1792-1804年のフランス最初の共和政。王政を廃止し樹立');
INSERT INTO terms (id, name, tier, category, description) VALUES (320, '恐怖政治', 1, 'フランス革命', '1793-94年のジャコバン派による恐怖政治。反対派を大量処刑した');
INSERT INTO terms (id, name, tier, category, description) VALUES (321, '徴兵制', 3, 'フランス革命', 'フランス革命で導入された国民皆兵制度。国民軍の形成につながった');
INSERT INTO terms (id, name, tier, category, description) VALUES (322, '第三身分', 2, 'フランス革命', 'フランス革命前の平民階級。第一身分聖職者・第二身分貴族以外');
INSERT INTO terms (id, name, tier, category, description) VALUES (323, 'ルイ16世処刑', 2, 'フランス革命', '1793年のルイ16世処刑。フランス王政の終焉を象徴する出来事');
INSERT INTO terms (id, name, tier, category, description) VALUES (324, 'ナポレオン', 1, 'フランス革命', 'フランスの軍人皇帝。ヨーロッパを席巻したがワーテルローで敗北し失脚');
INSERT INTO terms (id, name, tier, category, description) VALUES (325, 'ナポレオン法典', 1, 'フランス革命', 'ナポレオンが制定した民法典。私有財産権を保障し近代法の基礎となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (326, '神聖ローマ帝国の消滅', 2, 'フランス革命', '1806年の神聖ローマ帝国解体。ナポレオンによって千年帝国が終焉');
INSERT INTO terms (id, name, tier, category, description) VALUES (327, '大陸封鎖令', 2, 'フランス革命', 'ナポレオンの対英国経済封鎖令。大陸諸国に英国との貿易を禁止');
INSERT INTO terms (id, name, tier, category, description) VALUES (328, 'ウィーン会議', 1, 'ウィーン体制', '1814-15年のナポレオン後の国際会議。ヨーロッパ秩序再建を図った');
INSERT INTO terms (id, name, tier, category, description) VALUES (329, 'ウィーン体制', 2, 'ウィーン体制', 'ウィーン会議後の国際秩序。保守反動的で自由主義を抑圧した');
INSERT INTO terms (id, name, tier, category, description) VALUES (330, 'メッテルニヒ', 3, 'ウィーン体制', 'オーストリアの宰相。ウィーン体制の中心人物として保守反動を主導');
INSERT INTO terms (id, name, tier, category, description) VALUES (331, '正統主義', 3, 'ウィーン体制', 'ウィーン会議の原則。ナポレオン以前の王政復古を目指した');
INSERT INTO terms (id, name, tier, category, description) VALUES (332, 'ナショナリズム', 2, 'ウィーン体制', '民族の独立と統一を求める思想運動。19世紀ヨーロッパで展開');
INSERT INTO terms (id, name, tier, category, description) VALUES (333, '七月革命', 3, 'ウィーン体制', '1830年のフランス革命。ブルボン朝を倒し七月王政を樹立した');
INSERT INTO terms (id, name, tier, category, description) VALUES (334, 'トラファルガーの海戦', 2, 'フランス革命', '1805年のイギリス対仏西連合艦隊の海戦。英国海上覇権を確立');
INSERT INTO terms (id, name, tier, category, description) VALUES (335, 'アウステルリッツの戦い', 2, 'フランス革命', '1805年のナポレオン対墺露連合軍の戦い。三帝会戦での決定的勝利');
INSERT INTO terms (id, name, tier, category, description) VALUES (336, 'ワーテルローの戦い', 2, 'フランス革命', '1815年のナポレオン最終敗北。ワーテルローで英普連合軍に敗北');
INSERT INTO terms (id, name, tier, category, description) VALUES (337, '選挙法改正（第1回）', 3, '19世紀英米', '1832年のイギリス第1回選挙法改正。産業資本家に選挙権拡大');
INSERT INTO terms (id, name, tier, category, description) VALUES (338, 'チャーティスト運動', 3, '19世紀英米', '1830-40年代イギリスの労働者運動。人民憲章で普通選挙権要求');
INSERT INTO terms (id, name, tier, category, description) VALUES (339, '穀物法の廃止', 3, '19世紀英米', '1846年のイギリス穀物法廃止。自由貿易の勝利を象徴');
INSERT INTO terms (id, name, tier, category, description) VALUES (340, 'マルクス', 1, '産業革命', '科学的社会主義の創始者。資本論で資本主義を分析し社会主義を提唱');
INSERT INTO terms (id, name, tier, category, description) VALUES (341, '共産党宣言', 1, '産業革命', 'マルクスとエンゲルスが1848年に発表。万国の労働者団結を呼びかけた');
INSERT INTO terms (id, name, tier, category, description) VALUES (342, '1848年革命', 3, 'ウィーン体制', '1848年のヨーロッパ各地の革命。諸国民の春とも呼ばれた');
INSERT INTO terms (id, name, tier, category, description) VALUES (343, '二月革命', 3, 'ウィーン体制', '1848年のフランス革命。七月王政を倒し第二共和政を樹立した');
INSERT INTO terms (id, name, tier, category, description) VALUES (344, 'クリミア戦争', 2, 'ウィーン体制', '1853-56年の露土戦争。英仏がオスマン帝国を支援しロシアを破った');
INSERT INTO terms (id, name, tier, category, description) VALUES (345, 'ヴィクトリア女王', 2, '19世紀英米', '19世紀イギリス女王。在位63年で大英帝国最盛期を築いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (346, '二大政党制', 3, '19世紀英米', 'イギリス議会政治の特徴。保守党と自由党の二大政党制');
INSERT INTO terms (id, name, tier, category, description) VALUES (347, 'ナポレオン3世', 3, '国民国家形成', 'ナポレオン3世として第二帝政の皇帝。クーデタで権力掌握');
INSERT INTO terms (id, name, tier, category, description) VALUES (348, '普仏戦争', 1, '国民国家形成', '1870-71年のプロイセン対フランス戦争。ドイツ統一達成の契機');
INSERT INTO terms (id, name, tier, category, description) VALUES (349, 'パリ＝コミューン', 3, '国民国家形成', '1871年のパリ労働者自治政府。史上初の社会主義政権だが2か月で鎮圧');
INSERT INTO terms (id, name, tier, category, description) VALUES (350, 'イタリア統一', 2, '国民国家形成', '19世紀のイタリア統一運動。リソルジメントと呼ばれ1861年王国成立');
INSERT INTO terms (id, name, tier, category, description) VALUES (351, 'カヴール', 3, '国民国家形成', 'イタリア統一の政治的指導者。サルデーニャ首相として外交で統一推進');
INSERT INTO terms (id, name, tier, category, description) VALUES (352, 'ガリバルディ', 3, '国民国家形成', 'イタリア統一の軍事的指導者。千人隊を率いて両シチリア王国を征服');
INSERT INTO terms (id, name, tier, category, description) VALUES (353, 'ビスマルク', 2, '国民国家形成', 'プロイセン首相。鉄血政策でドイツ統一を達成し鉄血宰相と呼ばれた');
INSERT INTO terms (id, name, tier, category, description) VALUES (354, 'ドイツ帝国', 1, '国民国家形成', '1871年ヴェルサイユ宮殿で成立の統一ドイツ。プロイセン王が皇帝');
INSERT INTO terms (id, name, tier, category, description) VALUES (355, '三国同盟', 3, '国民国家形成', '1882年のドイツ・オーストリア・イタリア軍事同盟。対仏包囲網');
INSERT INTO terms (id, name, tier, category, description) VALUES (356, 'ラテンアメリカの独立', 2, 'アメリカ大陸', '19世紀初頭の中南米スペイン植民地の独立運動。多数の国家が成立');
INSERT INTO terms (id, name, tier, category, description) VALUES (357, 'シモン＝ボリバル', 2, 'アメリカ大陸', '南米独立運動の指導者。解放者と呼ばれ複数の国の独立を達成');
INSERT INTO terms (id, name, tier, category, description) VALUES (358, 'モンロー教書', 2, '19世紀英米', '1823年のモンロー大統領教書。ヨーロッパの新大陸不介入を要求');
INSERT INTO terms (id, name, tier, category, description) VALUES (359, 'モンロー主義', 3, '19世紀英米', 'モンロー教書に基づくアメリカ孤立主義。欧州不干渉と新大陸での優位');
INSERT INTO terms (id, name, tier, category, description) VALUES (360, 'マニフェスト＝デスティニー', 2, '19世紀英米', '明白な運命。西部への領土拡大は神の意志とするアメリカ膨張主義の理念');
INSERT INTO terms (id, name, tier, category, description) VALUES (361, '西漸運動', 3, '19世紀英米', 'アメリカの西部開拓運動。19世紀を通じて太平洋岸まで領土拡大');
INSERT INTO terms (id, name, tier, category, description) VALUES (362, 'フロンティア', 3, '19世紀英米', 'アメリカ西部開拓の最前線。フロンティアの消滅は発展段階の終わりとされた');
INSERT INTO terms (id, name, tier, category, description) VALUES (363, '南北戦争', 1, '19世紀英米', '1861-65年のアメリカ内戦。奴隷制廃止と連邦維持をめぐり戦った');
INSERT INTO terms (id, name, tier, category, description) VALUES (364, 'リンカン', 2, '19世紀英米', '南北戦争期の第16代アメリカ大統領。奴隷解放宣言を発布し連邦を守った');
INSERT INTO terms (id, name, tier, category, description) VALUES (365, '奴隷解放宣言', 2, '19世紀英米', '1863年リンカンが発した宣言。南部諸州の奴隷解放を宣言した');
INSERT INTO terms (id, name, tier, category, description) VALUES (366, 'ロマン主義', 3, '19世紀文化・思想', '19世紀前半の文化潮流。感情と想像力を重視し古典主義に対抗');
INSERT INTO terms (id, name, tier, category, description) VALUES (367, '写実主義', 3, '19世紀文化・思想', '19世紀中頃の文学芸術潮流。現実を客観的に描写することを重視');
INSERT INTO terms (id, name, tier, category, description) VALUES (368, '進化論', 1, '19世紀文化・思想', '生物は自然淘汰により進化するという理論。ダーウィンが提唱');
INSERT INTO terms (id, name, tier, category, description) VALUES (369, 'ヘーゲル', 2, '19世紀文化・思想', 'ドイツ観念論哲学の大成者。弁証法的歴史観を展開した');
INSERT INTO terms (id, name, tier, category, description) VALUES (370, 'ドストエフスキー', 2, '19世紀文化・思想', 'ロシア文学の巨匠。罪と罰やカラマーゾフの兄弟で人間の深層を描いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (371, 'トルストイ', 2, '19世紀文化・思想', 'ロシアの文豪。戦争と平和やアンナ・カレーニナで知られる');
INSERT INTO terms (id, name, tier, category, description) VALUES (372, 'タンジマート', 3, 'オスマン・西アジア', '1839年開始のオスマン帝国近代化改革。タンジマートは恩恵の意');
INSERT INTO terms (id, name, tier, category, description) VALUES (373, 'ミドハト憲法', 3, 'オスマン・西アジア', '1876年のオスマン帝国初の憲法。立憲君主制を規定したが翌年停止');
INSERT INTO terms (id, name, tier, category, description) VALUES (374, 'ムハンマド＝アリー', 3, 'オスマン・西アジア', 'エジプト太守。19世紀前半に近代化改革を推進しエジプト近代化の父');
INSERT INTO terms (id, name, tier, category, description) VALUES (375, 'ウラービー運動', 3, 'オスマン・西アジア', '1879-82年のエジプト反英運動。ウラービー大佐が率いた民族主義的抵抗');
INSERT INTO terms (id, name, tier, category, description) VALUES (376, 'アフガン戦争', 3, 'オスマン・西アジア', '1838-42年と1878-80年の英国とアフガニスタンの戦争。中央アジア支配を巡る対立');
INSERT INTO terms (id, name, tier, category, description) VALUES (377, 'インド大反乱', 1, 'インド近現代', '1857年のインド大反乱。シパーヒーの蜂起から始まった反英植民地闘争');
INSERT INTO terms (id, name, tier, category, description) VALUES (378, 'ムガル帝国の滅亡', 3, 'インド近現代', '1858年のムガル帝国滅亡。大反乱鎮圧後に英国が最後の皇帝を廃位');
INSERT INTO terms (id, name, tier, category, description) VALUES (379, 'インド帝国', 2, 'インド近現代', '1877年成立の英国によるインド直接統治体制。ヴィクトリア女王が皇帝に');
INSERT INTO terms (id, name, tier, category, description) VALUES (380, 'シパーヒーの反乱', 2, 'インド近現代', '1857年の東インド会社傭兵反乱。インド大反乱の発端となった蜂起');
INSERT INTO terms (id, name, tier, category, description) VALUES (381, '東インド会社解散', 3, 'インド近現代', '1858年の東インド会社解散。インド大反乱後に英国政府が直接統治へ');
INSERT INTO terms (id, name, tier, category, description) VALUES (382, 'アヘン戦争', 1, '中国近現代', '1840-42年の英清戦争。アヘン貿易を巡り英国が中国を武力で開国させた');
INSERT INTO terms (id, name, tier, category, description) VALUES (383, '南京条約', 1, '中国近現代', '1842年のアヘン戦争講和条約。香港割譲など中国初の不平等条約');
INSERT INTO terms (id, name, tier, category, description) VALUES (384, '不平等条約', 1, '中国近現代', '主権国家間の対等性を欠く条約。治外法権や関税自主権喪失など不利な内容');
INSERT INTO terms (id, name, tier, category, description) VALUES (385, '太平天国', 1, '明清', '1851-64年の中国農民反乱。洪秀全が天朝田畝制度など独自の理想社会を目指した');
INSERT INTO terms (id, name, tier, category, description) VALUES (386, '香港島の割譲', 3, '中国近現代', '1842年南京条約による香港島の英国割譲。アヘン戦争の結果150年続いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (387, '領事裁判権', 3, '中国近現代', '外国人が本国法で裁かれる特権。治外法権とも呼ばれ不平等条約の典型');
INSERT INTO terms (id, name, tier, category, description) VALUES (388, '租界', 3, '中国近現代', '中国の開港場に設けられた外国人居留地。治外法権下で独自の自治を行った');
INSERT INTO terms (id, name, tier, category, description) VALUES (389, '北京条約', 3, '中国近現代', '1860年のアロー戦争講和条約。天津条約を批准し北京を開放させた');
INSERT INTO terms (id, name, tier, category, description) VALUES (390, 'フランス領インドシナ連邦', 3, '東南アジア', '1887年成立の仏領インドシナ。ベトナム・カンボジア・ラオスを統合した植民地');
INSERT INTO terms (id, name, tier, category, description) VALUES (391, 'シンガポール', 3, '東南アジア', '1819年英国が獲得したマレー半島南端の港市。東南アジア貿易の要衝');
INSERT INTO terms (id, name, tier, category, description) VALUES (392, '李鴻章', 3, '中国近現代', '清末の政治家。洋務運動を主導し日清戦争講和や義和団事件処理に当たった');
INSERT INTO terms (id, name, tier, category, description) VALUES (393, '洋務運動', 2, '中国近現代', '1860-90年代の清朝近代化運動。西洋技術導入で富国強兵を目指した');
INSERT INTO terms (id, name, tier, category, description) VALUES (394, '日米修好通商条約', 3, '日本近現代', '1858年の日米通商条約。領事裁判権承認など日本初の不平等条約');
INSERT INTO terms (id, name, tier, category, description) VALUES (395, '日清戦争', 2, '日本近現代', '1894-95年の日清戦争。朝鮮支配を巡り日本が清に勝利し東アジアの勢力図を変えた');
INSERT INTO terms (id, name, tier, category, description) VALUES (396, '下関条約', 2, '日本近現代', '1895年の日清戦争講和条約。遼東半島・台湾割譲と朝鮮独立を認めさせた');
INSERT INTO terms (id, name, tier, category, description) VALUES (397, '第２次産業革命', 2, '帝国主義', '19世紀後半の技術革新。電力・化学・内燃機関が中心で独米が台頭した');
INSERT INTO terms (id, name, tier, category, description) VALUES (398, '帝国主義', 1, '帝国主義', '19世紀末の列強による植民地獲得競争。資本輸出と市場独占を特徴とした');
INSERT INTO terms (id, name, tier, category, description) VALUES (399, 'ボーア戦争', 2, '帝国主義', '1899-1902年の英国とボーア人の戦争。南アフリカ支配を巡った帝国主義戦争');
INSERT INTO terms (id, name, tier, category, description) VALUES (400, 'ドレフュス事件', 3, '国民国家形成', '1894年のユダヤ人将校冤罪事件。フランス社会を二分し反ユダヤ主義を露呈');
INSERT INTO terms (id, name, tier, category, description) VALUES (401, 'シオニズム', 3, '帝国主義', 'ユダヤ人国家建設運動。ドレフュス事件を機にヘルツルが提唱し後のイスラエルへ');
INSERT INTO terms (id, name, tier, category, description) VALUES (402, 'ヴィルヘルム２世', 3, '帝国主義', '1888-1918年在位のドイツ皇帝。世界政策で植民地獲得と海軍拡張を推進');
INSERT INTO terms (id, name, tier, category, description) VALUES (403, 'ドイツ社会民主党', 3, '帝国主義', '1875年結成のドイツ社会主義政党。マルクス主義を掲げ労働者政党として発展');
INSERT INTO terms (id, name, tier, category, description) VALUES (404, 'レーニン', 1, '帝国主義', 'ロシア革命の指導者。マルクス主義理論家で1917年ソヴィエト政権を樹立');
INSERT INTO terms (id, name, tier, category, description) VALUES (405, '日露戦争', 2, '日本近現代', '1904-05年の日露戦争。満州・朝鮮支配を巡り日本が勝利し世界を驚かせた');
INSERT INTO terms (id, name, tier, category, description) VALUES (406, '血の日曜日事件', 3, '帝国主義', '1905年1月の労働者デモ弾圧事件。ロシア第一革命の発端となった虐殺');
INSERT INTO terms (id, name, tier, category, description) VALUES (407, 'ソヴィエト', 3, '帝国主義', 'ロシア語で評議会。1905年革命で労働者代表機関として出現し17年革命で権力掌握');
INSERT INTO terms (id, name, tier, category, description) VALUES (408, '米西戦争', 3, '帝国主義', '1898年の米西戦争。米国がスペインを破りキューバ・フィリピンなど獲得した');
INSERT INTO terms (id, name, tier, category, description) VALUES (409, '門戸開放', 3, '帝国主義', '1899年のアメリカ対中国政策宣言。機会均等と領土保全を主張し門戸開放求めた');
INSERT INTO terms (id, name, tier, category, description) VALUES (410, 'パナマ運河', 3, '帝国主義', '1914年開通の運河。米国が建設し太平洋と大西洋を結ぶ海運の要衝となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (411, 'ファショダ事件', 3, 'アフリカ', '1898年の英仏対立事件。スーダンのファショダで両国軍が衝突寸前まで対峙');
INSERT INTO terms (id, name, tier, category, description) VALUES (412, '三国干渉', 3, '帝国主義', '1895年の露独仏による日本への圧力。遼東半島返還を要求し日本は屈服した');
INSERT INTO terms (id, name, tier, category, description) VALUES (413, '日英同盟', 2, '帝国主義', '1902年の日英同盟。ロシア南下政策に対抗し日本の国際的地位を高めた');
INSERT INTO terms (id, name, tier, category, description) VALUES (414, '三国協商', 3, '帝国主義', '1907年の英露協商成立で完成した英仏露三国協調体制。対独包囲網形成へ');
INSERT INTO terms (id, name, tier, category, description) VALUES (415, '義和団事件', 3, '中国近現代', '1900年の排外運動と列強共同出兵。義和団が扶清滅洋を唱え北京を占拠した');
INSERT INTO terms (id, name, tier, category, description) VALUES (416, 'ポーツマス条約', 2, '日本近現代', '1905年の日露戦争講和条約。ポーツマスで調印し南樺太や旅順など獲得');
INSERT INTO terms (id, name, tier, category, description) VALUES (417, '韓国併合', 2, '日本近現代', '1910年の日本による朝鮮併合。韓国を植民地化し朝鮮総督府による統治開始');
INSERT INTO terms (id, name, tier, category, description) VALUES (418, 'メキシコ革命', 3, 'アメリカ大陸', '1910年のメキシコ革命。ディアス独裁を倒し土地改革など社会変革を目指した');
INSERT INTO terms (id, name, tier, category, description) VALUES (419, '露仏同盟', 3, '帝国主義', '1891-94年の露仏軍事同盟。ドイツ三国同盟に対抗し二大陣営対立の始まり');
INSERT INTO terms (id, name, tier, category, description) VALUES (420, '英仏協商', 3, '帝国主義', '1904年の英仏協商。植民地問題で妥協しドイツ包囲網形成の第一歩となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (421, '戊戌の変法', 3, '中国近現代', '1898年の清朝改革運動。光緒帝と康有為が立憲君主制目指すも西太后が阻止');
INSERT INTO terms (id, name, tier, category, description) VALUES (422, '孫文', 1, '中国近現代', '中国革命の父。三民主義を唱え辛亥革命を指導し中華民国臨時大総統となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (423, '三民主義', 3, '中国近現代', '孫文の革命思想。民族独立・民権伸張・民生安定の三原則で中国革命を導いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (424, '辛亥革命', 1, '中国近現代', '1911年の中国革命。清朝を倒し共和制樹立でアジア初の共和国が誕生した');
INSERT INTO terms (id, name, tier, category, description) VALUES (425, '中華民国', 3, '中国近現代', '1912年成立の中華民国。辛亥革命で清朝を倒しアジア初の共和国となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (426, '袁世凱', 3, '中国近現代', '清末民初の軍人政治家。辛亥革命後に実権掌握し帝政復活を企てた');
INSERT INTO terms (id, name, tier, category, description) VALUES (427, 'インド国民会議', 2, 'インド近現代', '1885年結成のインド民族運動組織。英国統治下で自治獲得を目指した');
INSERT INTO terms (id, name, tier, category, description) VALUES (428, 'サライェヴォ事件', 2, '第一次世界大戦', '1914年6月のオーストリア皇太子暗殺事件。第一次世界大戦勃発の引き金');
INSERT INTO terms (id, name, tier, category, description) VALUES (429, '第一次世界大戦', 1, '第一次世界大戦', '1914-18年の世界大戦。総力戦で欧州を荒廃させ1000万人以上が犠牲に');
INSERT INTO terms (id, name, tier, category, description) VALUES (430, 'スワラージ', 2, 'インド近現代', 'ヒンディー語で自治。ガンディーらが唱えたインド独立運動の目標');
INSERT INTO terms (id, name, tier, category, description) VALUES (431, '青年トルコ革命', 3, 'オスマン・西アジア', '1908年のオスマン帝国立憲革命。青年トルコ人が専制君主制打倒を目指した');
INSERT INTO terms (id, name, tier, category, description) VALUES (432, 'サイクス・ピコ協定', 3, '第一次世界大戦', '1916年の英仏露秘密協定。オスマン帝国領分割を定め戦後中東混乱の原因に');
INSERT INTO terms (id, name, tier, category, description) VALUES (433, 'バルフォア宣言', 3, '第一次世界大戦', '1917年の英国によるユダヤ人国家建設支持宣言。パレスチナ問題の起源');
INSERT INTO terms (id, name, tier, category, description) VALUES (434, '十四カ条', 3, '第一次世界大戦', '1918年ウィルソンの戦後構想。民族自決や国際連盟など14項目の平和原則');
INSERT INTO terms (id, name, tier, category, description) VALUES (435, '十月革命', 2, 'ロシア・ソ連', '1917年11月のボリシェヴィキ革命。レーニンが権力掌握し世界初の社会主義国家樹立');
INSERT INTO terms (id, name, tier, category, description) VALUES (436, '民族自決', 2, '第一次世界大戦', '各民族が自らの運命を決定する権利。ウィルソン十四カ条で提唱され植民地に影響');
INSERT INTO terms (id, name, tier, category, description) VALUES (437, 'コミンテルン', 3, 'ロシア・ソ連', '1919年設立の国際共産主義組織。第三インターナショナルで世界革命を指導');
INSERT INTO terms (id, name, tier, category, description) VALUES (438, 'ソ連', 1, 'ロシア・ソ連', '1922年成立の世界初の社会主義国家。ソヴィエト社会主義共和国連邦の略称');
INSERT INTO terms (id, name, tier, category, description) VALUES (439, 'パリ講和会議', 2, '第二次世界大戦', '1919年のパリ講和会議。第一次大戦後処理とヴェルサイユ体制を確立した');
INSERT INTO terms (id, name, tier, category, description) VALUES (440, 'ヴェルサイユ条約', 1, '第二次世界大戦', '1919年の対独講和条約。巨額賠償と軍備制限でドイツに過酷な条件を課した');
INSERT INTO terms (id, name, tier, category, description) VALUES (441, '国際連盟', 1, '第二次世界大戦', '1920年発足の国際平和機構。史上初の集団安全保障だが米国不参加で弱体');
INSERT INTO terms (id, name, tier, category, description) VALUES (442, 'トロツキー', 2, 'ロシア・ソ連', 'ロシア革命の指導者。赤軍を組織したがスターリンとの権力闘争に敗れた');
INSERT INTO terms (id, name, tier, category, description) VALUES (443, 'ヴェルサイユ体制', 2, '戦間期', '第一次大戦後のヴェルサイユ条約を基軸とした国際秩序。1930年代に崩壊');
INSERT INTO terms (id, name, tier, category, description) VALUES (444, 'ワシントン会議', 3, '戦間期', '1921-22年のワシントン軍縮会議。海軍制限と太平洋・中国問題を協議');
INSERT INTO terms (id, name, tier, category, description) VALUES (445, '九カ国条約', 3, '戦間期', '1922年の中国に関する九カ国条約。主権尊重と門戸開放を約した');
INSERT INTO terms (id, name, tier, category, description) VALUES (446, 'ワシントン体制', 3, '戦間期', 'ワシントン会議で成立した東アジア・太平洋の国際秩序。日本の膨張を抑制');
INSERT INTO terms (id, name, tier, category, description) VALUES (447, 'ワイマール憲法', 2, '戦間期', '1919年のドイツ民主憲法。社会権保障など先進的内容だが非常事態条項が弱点');
INSERT INTO terms (id, name, tier, category, description) VALUES (448, 'ヒトラー', 1, 'ファシズム', 'ナチス党指導者。1933年首相就任後に独裁体制確立し第二次大戦を引き起こした');
INSERT INTO terms (id, name, tier, category, description) VALUES (449, 'ファシスト党', 2, 'ファシズム', '1921年ムッソリーニが結成したイタリア・ファシスト党。暴力で政権掌握');
INSERT INTO terms (id, name, tier, category, description) VALUES (450, 'ムッソリーニ', 1, 'ファシズム', 'イタリア・ファシズムの指導者。1922年ローマ進軍で政権掌握し独裁体制樹立');
INSERT INTO terms (id, name, tier, category, description) VALUES (451, 'ファシズム', 1, '戦間期', '全体主義的独裁体制。反共・国家主義で1920-30年代に伊独など各国で台頭');
INSERT INTO terms (id, name, tier, category, description) VALUES (452, 'スターリン', 1, 'ロシア・ソ連', 'ソ連の独裁者。1920年代に権力掌握し急速工業化と大粛清で恐怖政治を敷いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (453, '魯迅', 3, '中国近現代', '中国近代文学の父。白話文学運動を主導し魯迅の筆名で社会批判の作品を書いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (454, '五・四運動', 2, '中国近現代', '1919年の中国反帝国主義運動。ヴェルサイユ条約に抗議し新文化運動を加速');
INSERT INTO terms (id, name, tier, category, description) VALUES (455, '中国国民党', 2, '中国近現代', '1919年改組の中国国民党。孫文が率い後に蔣介石が継承し中国統一を目指した');
INSERT INTO terms (id, name, tier, category, description) VALUES (456, '中国共産党', 2, '中国近現代', '1921年結成の中国共産党。マルクス主義政党で毛沢東が後に指導権掌握');
INSERT INTO terms (id, name, tier, category, description) VALUES (457, '第１次国共合作', 3, '中国近現代', '1924-27年の国共合作。孫文の提唱で国民党と共産党が協力し北伐を実施');
INSERT INTO terms (id, name, tier, category, description) VALUES (458, '蔣介石', 2, '中国近現代', '国民党指導者。北伐を完遂し南京国民政府を樹立したが日中戦争で敗北');
INSERT INTO terms (id, name, tier, category, description) VALUES (459, '北伐', 3, '中国近現代', '1926-28年の国民革命軍北伐。蔣介石が軍閥打倒し中国統一をほぼ達成した');
INSERT INTO terms (id, name, tier, category, description) VALUES (460, '毛沢東', 1, '中国近現代', '中国共産党の創設期指導者。長征を率い1949年に中華人民共和国を建国した');
INSERT INTO terms (id, name, tier, category, description) VALUES (461, 'ガンディー', 1, 'インド近現代', 'インド独立運動の指導者。非暴力・不服従の抵抗で英国支配からの独立を達成');
INSERT INTO terms (id, name, tier, category, description) VALUES (462, 'ネルー', 2, 'インド近現代', 'インド独立運動の指導者。ガンディーの後継者で初代首相として国家建設を主導');
INSERT INTO terms (id, name, tier, category, description) VALUES (463, '非暴力・不服従運動', 1, 'インド近現代', 'ガンディーの抵抗思想。武力を用いず法への不服従で英国支配に抵抗した');
INSERT INTO terms (id, name, tier, category, description) VALUES (464, '塩の行進', 2, 'インド近現代', '1930年のガンディー主導の抗議行進。塩専売法に抗議し不服従運動を展開');
INSERT INTO terms (id, name, tier, category, description) VALUES (465, 'ムスタファ・ケマル', 2, 'オスマン・西アジア', 'トルコ共和国建国の父。ケマル・アタテュルクとも呼ばれ近代化を強力に推進');
INSERT INTO terms (id, name, tier, category, description) VALUES (466, 'トルコ共和国', 3, 'オスマン・西アジア', '1923年成立のトルコ共和国。ムスタファ・ケマルがオスマン帝国を倒し建国');
INSERT INTO terms (id, name, tier, category, description) VALUES (467, '世界恐慌', 1, '戦間期', '1929年から始まった世界規模の経済恐慌。資本主義体制を揺るがす未曾有の危機');
INSERT INTO terms (id, name, tier, category, description) VALUES (468, 'ニューディール政策', 1, '戦間期', 'ローズヴェルトの恐慌対策。政府介入で経済再建と社会保障拡充を図った');
INSERT INTO terms (id, name, tier, category, description) VALUES (469, 'フランクリン＝ローズヴェルト', 2, 'アメリカ現代', '米国第32代大統領。ニューディール政策で恐慌克服し第二次大戦を指導した');
INSERT INTO terms (id, name, tier, category, description) VALUES (470, 'ブロック経済', 2, '戦間期', '世界恐慌下の排他的経済圏。英仏など植民地を持つ国が特恵関税で囲い込んだ');
INSERT INTO terms (id, name, tier, category, description) VALUES (471, '満州事変', 2, '日本近現代', '1931年の日本による満州占領事件。関東軍の謀略で柳条湖事件を起こした');
INSERT INTO terms (id, name, tier, category, description) VALUES (472, '満州国', 3, '日本近現代', '1932年日本が建てた満州の傀儡国家。清朝最後の皇帝溥儀を執政に据えた');
INSERT INTO terms (id, name, tier, category, description) VALUES (473, '日中戦争', 1, '日本近現代', '1937-45年の日中全面戦争。盧溝橋事件を発端に泥沼の消耗戦となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (474, '日本の国際連盟脱退', 3, '日本近現代', '1933年の日本の国際連盟脱退。満州国不承認のリットン報告書採択に反発');
INSERT INTO terms (id, name, tier, category, description) VALUES (475, '南京事件', 3, '日本近現代', '1937年の日中戦争における大規模虐殺事件。南京占領時に多数の民間人が犠牲に');
INSERT INTO terms (id, name, tier, category, description) VALUES (476, 'ナチス・ドイツ', 1, 'ファシズム', '1933-45年のヒトラー独裁国家。第三帝国とも呼ばれ全体主義体制を敷いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (477, '全権委任法', 2, 'ファシズム', '1933年のナチス全権委任法。議会を無力化しヒトラー独裁の法的基盤となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (478, '強制収容所', 2, 'ファシズム', 'ナチスが政治犯やユダヤ人を収容した施設。強制労働と絶滅の場となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (479, '独ソ不可侵条約', 2, '第二次世界大戦', '1939年8月の独ソ不可侵条約。密約でポーランド分割を定め世界を驚愕させた');
INSERT INTO terms (id, name, tier, category, description) VALUES (480, '第二次世界大戦', 1, '第二次世界大戦', '1939-45年の世界大戦。史上最大の戦争で5000万人以上が犠牲となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (481, '宥和政策', 2, '第二次世界大戦', '1930年代の英仏対独宥和政策。ヒトラーの要求を容認し戦争回避を図った');
INSERT INTO terms (id, name, tier, category, description) VALUES (482, 'ミュンヘン会談', 2, '第二次世界大戦', '1938年の英仏独伊会談。チェコ割譲を認め宥和政策の頂点となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (483, 'スペイン内戦', 3, '主権国家形成', '1936-39年のスペイン内戦。人民戦線とフランコ軍の戦いで第二次大戦の前哨戦');
INSERT INTO terms (id, name, tier, category, description) VALUES (484, 'フランスの降伏', 3, '第二次世界大戦', '1940年6月のフランス降伏。ドイツ電撃戦で6週間で陥落しヴィシー政権成立');
INSERT INTO terms (id, name, tier, category, description) VALUES (485, 'チャーチル', 2, '第二次世界大戦', '英国首相。第二次大戦で不屈の抵抗精神を示し連合国勝利を導いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (486, '独ソ戦', 1, '第二次世界大戦', '1941-45年の独ソ戦争。史上最大の地上戦で2000万人以上のソ連人が犠牲に');
INSERT INTO terms (id, name, tier, category, description) VALUES (487, 'ホロコースト', 1, '第二次世界大戦', 'ナチスのユダヤ人大量虐殺。600万人が組織的絶滅政策の犠牲となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (488, '太平洋戦争', 1, '日本近現代', '1941-45年の日米英蘭戦争。真珠湾攻撃で始まりアジア太平洋全域に拡大');
INSERT INTO terms (id, name, tier, category, description) VALUES (489, '真珠湾攻撃', 2, '日本近現代', '1941年12月8日の日本による奇襲攻撃。太平洋戦争開始の電撃作戦');
INSERT INTO terms (id, name, tier, category, description) VALUES (490, '大東亜共栄圏', 3, '日本近現代', '日本が掲げた東アジア支配構想。欧米排除の名目で植民地支配を正当化');
INSERT INTO terms (id, name, tier, category, description) VALUES (491, 'ヤルタ会談', 1, '第二次世界大戦', '1945年2月の米英ソ首脳会談。戦後処理とソ連対日参戦を秘密裏に協議');
INSERT INTO terms (id, name, tier, category, description) VALUES (492, 'ポツダム宣言', 2, '第二次世界大戦', '1945年7月の対日降伏勧告宣言。無条件降伏を要求し日本がポツダムで受諾');
INSERT INTO terms (id, name, tier, category, description) VALUES (493, '原子爆弾', 1, '第二次世界大戦', '核分裂エネルギーを利用した大量破壊兵器。広島・長崎で初めて実戦使用された');
INSERT INTO terms (id, name, tier, category, description) VALUES (494, '国際連合', 1, '冷戦', '1945年発足の国際平和機構。国際連盟の反省を踏まえ集団安全保障を強化');
INSERT INTO terms (id, name, tier, category, description) VALUES (495, '広島・長崎', 2, '日本近現代', '1945年8月の原爆投下都市。広島6日・長崎9日で計20万人以上が犠牲に');
INSERT INTO terms (id, name, tier, category, description) VALUES (496, '常任理事国', 1, '国際関係', '国連安保理の五大国。米英仏露中でP5とも呼ばれ拒否権を持つ');
INSERT INTO terms (id, name, tier, category, description) VALUES (497, '拒否権', 2, '国際関係', '安保理常任理事国の特権。決議案に対する単独否決権で重要決定を阻止できる');
INSERT INTO terms (id, name, tier, category, description) VALUES (498, '世界人権宣言', 1, '国際関係', '1948年の国連人権宣言。基本的人権を国際的に初めて体系的に規定した');
INSERT INTO terms (id, name, tier, category, description) VALUES (499, 'ブレトンウッズ体制', 2, '冷戦', '1944年成立の国際通貨体制。金・ドル本位制で戦後経済秩序の基盤となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (500, 'IMF', 3, '冷戦', '1944年設立の国際通貨基金。為替安定と国際収支調整を目的とする機関');
INSERT INTO terms (id, name, tier, category, description) VALUES (501, '日本国憲法', 2, '東アジア', '1947年施行の日本国憲法。国民主権・基本的人権・平和主義を三原則とする');
INSERT INTO terms (id, name, tier, category, description) VALUES (502, '冷戦', 1, '冷戦', '1945-91年の米ソ対立。冷たい戦争とも呼ばれ熱戦なき東西陣営の対決');
INSERT INTO terms (id, name, tier, category, description) VALUES (503, 'トルーマン＝ドクトリン', 3, '冷戦', '1947年のトルーマン対ソ封じ込め政策。共産主義拡大阻止を宣言した');
INSERT INTO terms (id, name, tier, category, description) VALUES (504, 'マーシャル＝プラン', 1, '冷戦', '1947年の欧州復興援助計画。米国が西欧経済再建を支援し冷戦構造を固めた');
INSERT INTO terms (id, name, tier, category, description) VALUES (505, 'NATO', 1, '冷戦', '1949年成立の北大西洋条約機構。西側軍事同盟で対ソ集団防衛体制を構築');
INSERT INTO terms (id, name, tier, category, description) VALUES (506, '鉄のカーテン', 2, '冷戦', 'チャーチルが1946年演説で使った表現。欧州を東西に分断する冷戦の象徴');
INSERT INTO terms (id, name, tier, category, description) VALUES (507, '中華人民共和国', 1, '中国近現代', '1949年成立の中国社会主義国家。毛沢東が建国し世界最大の共産圏となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (508, '周恩来', 3, '中国近現代', '中華人民共和国初代首相。周恩来は外交を主導し中国の国際的地位を高めた');
INSERT INTO terms (id, name, tier, category, description) VALUES (509, '中ソ友好同盟相互援助条約', 3, '冷戦', '1950年の中ソ同盟条約。社会主義陣営の連帯を示し対米包囲網を形成した');
INSERT INTO terms (id, name, tier, category, description) VALUES (510, '韓国', 3, '東アジア', '1948年成立の朝鮮半島南部の国家。大韓民国が正式名称で米国が支援');
INSERT INTO terms (id, name, tier, category, description) VALUES (511, '北朝鮮', 3, '東アジア', '1948年成立の朝鮮半島北部の国家。朝鮮民主主義人民共和国でソ連が支援');
INSERT INTO terms (id, name, tier, category, description) VALUES (512, 'パレスチナ問題', 1, '中東', 'パレスチナの帰属を巡るアラブとイスラエルの対立。中東紛争の根本問題');
INSERT INTO terms (id, name, tier, category, description) VALUES (513, 'イスラエル', 1, '中東', '1948年建国のユダヤ人国家。パレスチナ分割決議に基づき中東に誕生した');
INSERT INTO terms (id, name, tier, category, description) VALUES (514, 'ナセル', 2, '中東', 'エジプト第2代大統領。アラブ民族主義を掲げスエズ運河国有化を断行した');
INSERT INTO terms (id, name, tier, category, description) VALUES (515, '朝鮮戦争', 1, '東アジア', '1950-53年の朝鮮戦争。南北分断が固定化し冷戦の熱戦となった代理戦争');
INSERT INTO terms (id, name, tier, category, description) VALUES (516, 'サンフランシスコ平和条約', 2, '東アジア', '1951年の対日講和条約。サンフランシスコで調印し日本の主権回復を実現');
INSERT INTO terms (id, name, tier, category, description) VALUES (517, '水素爆弾', 2, '冷戦', '核融合反応を利用した大量破壊兵器。原爆の数百倍の威力を持つ究極兵器');
INSERT INTO terms (id, name, tier, category, description) VALUES (518, 'スターリン批判', 2, 'ロシア・ソ連', '1956年のフルシチョフによるスターリン批判。個人崇拝を否定し雪解けを促した');
INSERT INTO terms (id, name, tier, category, description) VALUES (519, 'フルシチョフ', 3, 'ロシア・ソ連', 'ソ連指導者。スターリン批判で雪解けを推進したが米ソ対立も激化させた');
INSERT INTO terms (id, name, tier, category, description) VALUES (520, 'ベルリンの壁', 1, '冷戦', '1961年建設の東西ベルリン分断壁。冷戦の象徴で1989年崩壊まで存続');
INSERT INTO terms (id, name, tier, category, description) VALUES (521, 'キューバ危機', 1, '冷戦', '1962年の米ソ核戦争危機。キューバ配備ミサイルを巡り人類滅亡の瀬戸際に');
INSERT INTO terms (id, name, tier, category, description) VALUES (522, 'EEC', 3, '欧州統合', '1958年発足のヨーロッパ経済共同体。欧州統合の中核でEUの前身となった');
INSERT INTO terms (id, name, tier, category, description) VALUES (523, 'バンドン会議', 2, '国際関係', '1955年のアジア・アフリカ会議。インドネシアのバンドンで第三世界が結集');
INSERT INTO terms (id, name, tier, category, description) VALUES (524, '非同盟主義', 2, '国際関係', '米ソいずれにも属さない中立外交路線。第三世界諸国が冷戦下で追求した');
INSERT INTO terms (id, name, tier, category, description) VALUES (525, 'スエズ運河国有化', 3, '中東', '1956年のナセルによるスエズ運河国有化宣言。英仏の利権に挑戦し第2次中東戦争の発端に');
INSERT INTO terms (id, name, tier, category, description) VALUES (526, 'アフリカの年', 3, 'アフリカ', '1960年のアフリカ諸国大量独立。17か国が独立し脱植民地化が加速した年');
INSERT INTO terms (id, name, tier, category, description) VALUES (527, 'キューバ革命', 2, 'アメリカ大陸', '1959年のキューバ革命。カストロがバティスタ独裁を倒し社会主義政権樹立');
INSERT INTO terms (id, name, tier, category, description) VALUES (528, 'カストロ', 2, 'アメリカ大陸', 'キューバ革命の指導者。1959年政権掌握し米国に対抗する社会主義体制を築いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (529, '中ソ対立', 3, '冷戦', '1960年代の中ソ対立。イデオロギー対立で社会主義陣営が分裂した');
INSERT INTO terms (id, name, tier, category, description) VALUES (530, 'プロレタリア文化大革命', 2, '中国近現代', '1966-76年の中国文化大革命。毛沢東主導の極左運動で社会が大混乱に陥った');
INSERT INTO terms (id, name, tier, category, description) VALUES (531, '鄧小平', 3, '中国近現代', '中国の改革開放指導者。鄧小平理論で市場経済導入し現代中国の基礎を築いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (532, 'ベトナム戦争', 1, '国際関係', '1960-75年のベトナム戦争。南北統一を巡る内戦に米国が介入し泥沼化した');
INSERT INTO terms (id, name, tier, category, description) VALUES (533, 'ニクソン', 2, 'アメリカ現代', '米国第37代大統領。ベトナム撤退と訪中で外交転換したがウォーターゲート事件で辞任');
INSERT INTO terms (id, name, tier, category, description) VALUES (534, 'パリ和平協定', 3, '国際関係', '1973年のベトナム和平協定。パリ協定で米軍撤退が決定したが戦争は継続');
INSERT INTO terms (id, name, tier, category, description) VALUES (535, 'サイゴン陥落', 3, '国際関係', '1975年4月のサイゴン陥落。北ベトナム軍が南ベトナムの首都を占領し戦争終結');
INSERT INTO terms (id, name, tier, category, description) VALUES (536, '公民権運動', 2, 'アメリカ現代', '1950-60年代の米国黒人解放運動。人種差別撤廃と平等な市民権獲得を目指した');
INSERT INTO terms (id, name, tier, category, description) VALUES (537, 'キング牧師', 2, 'アメリカ現代', '米国公民権運動の指導者。非暴力抵抗でキング牧師は人種差別撤廃を訴えた');
INSERT INTO terms (id, name, tier, category, description) VALUES (538, '公民権法', 3, 'アメリカ現代', '1964年の米国公民権法。人種差別を法的に禁止し黒人の権利を保障した');
INSERT INTO terms (id, name, tier, category, description) VALUES (539, 'ASEAN', 2, '東南アジア', '1967年発足の東南アジア諸国連合。地域協力と経済発展を目指す機構');
INSERT INTO terms (id, name, tier, category, description) VALUES (540, 'オイルショック', 2, '国際関係', '1973年の第1次石油危機。原油価格高騰でオイルショックが世界経済を直撃');
INSERT INTO terms (id, name, tier, category, description) VALUES (541, 'OPEC', 2, '中東', '1960年設立の石油輸出国機構。産油国カルテルで原油価格を統制した');
INSERT INTO terms (id, name, tier, category, description) VALUES (542, 'NPT', 2, '現代', '1968年の核拡散防止条約。核保有国を五大国に限定し核軍縮を目指した');
INSERT INTO terms (id, name, tier, category, description) VALUES (543, 'デタント', 3, '冷戦', '1960-70年代の米ソ緊張緩和。冷戦の一時的融和期でデタントと呼ばれた');
INSERT INTO terms (id, name, tier, category, description) VALUES (544, 'マルタ会談', 3, '冷戦', '1989年12月の米ソ首脳会談。マルタ会談で冷戦終結を宣言した歴史的会談');
INSERT INTO terms (id, name, tier, category, description) VALUES (545, '冷戦の終結', 1, '冷戦', '1989-91年の冷戦終結。ベルリンの壁崩壊とソ連解体で東西対立が消滅');
INSERT INTO terms (id, name, tier, category, description) VALUES (546, '日中国交正常化', 3, '東アジア', '1972年の日中国交正常化。田中角栄訪中で日本と中国が国交樹立した');
INSERT INTO terms (id, name, tier, category, description) VALUES (547, 'サミット/G7', 2, '国際関係', '1975年開始の主要国首脳会議。先進7か国が経済・外交を協議するサミット');
INSERT INTO terms (id, name, tier, category, description) VALUES (548, '新自由主義', 3, '19世紀文化・思想', '1980年代の経済思想。市場原理と規制緩和を重視し小さな政府を目指した');
INSERT INTO terms (id, name, tier, category, description) VALUES (549, 'ドイツ統一', 2, '冷戦', '1990年の東西ドイツ統一。冷戦終結の象徴で分断45年ぶりの再統一実現');
INSERT INTO terms (id, name, tier, category, description) VALUES (550, 'EU', 1, '欧州統合', '1993年発足の欧州連合。経済統合から政治統合へ進む欧州の地域機構');
INSERT INTO terms (id, name, tier, category, description) VALUES (551, 'ユーロ', 2, '欧州統合', '1999年導入の欧州単一通貨。EU加盟国の共通通貨でユーロと呼ばれる');
INSERT INTO terms (id, name, tier, category, description) VALUES (552, 'ゴルバチョフ', 3, 'ロシア・ソ連', 'ソ連最後の指導者。ペレストロイカで改革推進したが結果的にソ連崩壊を招いた');
INSERT INTO terms (id, name, tier, category, description) VALUES (553, 'ペレストロイカ', 3, 'ロシア・ソ連', 'ゴルバチョフの改革路線。ソ連の立て直しを図ったが体制崩壊につながった');
INSERT INTO terms (id, name, tier, category, description) VALUES (554, 'ソ連の解体', 1, '冷戦', '1991年のソ連解体。15共和国が独立し69年続いた社会主義国家が消滅');
INSERT INTO terms (id, name, tier, category, description) VALUES (555, '東欧革命', 2, '冷戦', '1989年の東欧民主化革命。共産党政権が次々と崩壊し冷戦終結を加速した');
INSERT INTO terms (id, name, tier, category, description) VALUES (556, 'ビロード革命', 3, 'ビザンツ・東欧', '1989年のチェコスロヴァキア民主化革命。無血革命でビロード革命と呼ばれた');
INSERT INTO terms (id, name, tier, category, description) VALUES (557, 'ユーゴスラヴィア解体', 3, 'ビザンツ・東欧', '1991-92年のユーゴスラヴィア解体。民族対立で内戦を経て6共和国に分裂');
INSERT INTO terms (id, name, tier, category, description) VALUES (558, 'ワルシャワ条約機構の解散', 3, '冷戦', '1991年のワルシャワ条約機構解散。東側軍事同盟が消滅し冷戦終結を象徴');
INSERT INTO terms (id, name, tier, category, description) VALUES (559, '改革開放政策', 2, '中国近現代', '1978年開始の中国改革開放。鄧小平が主導し市場経済導入で経済発展を実現');
INSERT INTO terms (id, name, tier, category, description) VALUES (560, '天安門事件（第2次）', 2, '中国近現代', '1989年6月の天安門事件。民主化運動を中国政府が武力弾圧し多数の犠牲者');
INSERT INTO terms (id, name, tier, category, description) VALUES (561, '香港返還', 3, '中国近現代', '1997年の香港返還。英国から中国へ150年ぶりの主権回復で一国二制度開始');
INSERT INTO terms (id, name, tier, category, description) VALUES (562, 'ドイモイ', 3, '東南アジア', '1986年開始のベトナム刷新政策。市場経済導入でドイモイと呼ばれる改革');
INSERT INTO terms (id, name, tier, category, description) VALUES (563, 'カンボジア和平協定', 3, '東南アジア', '1991年のカンボジア和平協定。20年以上の内戦終結でパリ協定を締結');
INSERT INTO terms (id, name, tier, category, description) VALUES (564, '南北問題', 3, '国際関係', '先進国と途上国の経済格差問題。南北問題として1960年代から深刻化した');
INSERT INTO terms (id, name, tier, category, description) VALUES (565, '第3次中東戦争', 3, '中東', '1967年の第3次中東戦争。イスラエルが6日間でアラブ諸国を圧倒した');
INSERT INTO terms (id, name, tier, category, description) VALUES (566, 'PLO', 2, '中東', 'パレスチナ解放機構。1964年結成でアラファトが率いパレスチナ独立を目指した');
INSERT INTO terms (id, name, tier, category, description) VALUES (567, '第4次中東戦争', 3, '中東', '1973年の第4次中東戦争。エジプトとシリアが奇襲もイスラエルが反撃');
INSERT INTO terms (id, name, tier, category, description) VALUES (568, 'オスロ合意', 3, '中東', '1993年のパレスチナ暫定自治合意。オスロ合意でPLOとイスラエルが和解');
INSERT INTO terms (id, name, tier, category, description) VALUES (569, 'イラン革命', 2, '中東', '1979年のイラン革命。ホメイニが王政を倒しイスラム共和国を樹立した');
INSERT INTO terms (id, name, tier, category, description) VALUES (570, 'イラン＝イラク戦争', 3, '中東', '1980-88年のイラン・イラク戦争。8年の消耗戦で両国に甚大な被害');
INSERT INTO terms (id, name, tier, category, description) VALUES (571, '9.11同時多発テロ', 2, '国際関係', '2001年9月11日の米国同時多発テロ。アルカイダによる史上最悪のテロ攻撃');
INSERT INTO terms (id, name, tier, category, description) VALUES (572, 'アル＝カーイダ', 3, '国際関係', '国際テロ組織。ビンラディンが率い9.11テロなど反米テロを実行した');
INSERT INTO terms (id, name, tier, category, description) VALUES (573, 'アフガニスタン戦争', 2, '中東', '2001年開始の対テロ戦争。米国が9.11テロ後にアフガニスタンを攻撃した');
INSERT INTO terms (id, name, tier, category, description) VALUES (574, 'アジア通貨危機', 3, '東アジア', '1997年の東南アジア通貨危機。タイから始まり域内各国経済が連鎖的に混乱');
INSERT INTO terms (id, name, tier, category, description) VALUES (575, 'マンデラ', 2, 'アフリカ', '南アフリカ初の黒人大統領。27年の獄中生活を経てアパルトヘイト撤廃を実現');
INSERT INTO terms (id, name, tier, category, description) VALUES (576, 'アラブの春', 3, '中東', '2010-12年の中東民主化運動。チュニジアから広がり独裁政権が次々崩壊');
INSERT INTO terms (id, name, tier, category, description) VALUES (577, 'シリア内戦', 3, '中東', '2011年開始のシリア内戦。アサド政権と反体制派の内戦で50万人以上が犠牲');
INSERT INTO terms (id, name, tier, category, description) VALUES (578, 'イスラム国（IS）', 3, '中東', '2014年樹立の過激派組織。イスラム国を自称しシリア・イラクで支配地域を拡大');
INSERT INTO terms (id, name, tier, category, description) VALUES (579, 'ヨーロッパ難民危機', 3, '欧州統合', '2015年の欧州難民危機。シリア内戦などで100万人以上の難民が欧州に流入');
INSERT INTO terms (id, name, tier, category, description) VALUES (580, 'イギリスのEU離脱', 3, '欧州統合', '2020年のイギリスEU離脱。ブレグジットで47年ぶりに欧州統合から離脱');
INSERT INTO terms (id, name, tier, category, description) VALUES (581, 'ロシアのウクライナ侵攻', 2, '国際関係', '2022年開始のロシア・ウクライナ戦争。プーチンが侵攻し国際秩序を揺るがした');
INSERT INTO terms (id, name, tier, category, description) VALUES (582, 'クリミア危機', 3, 'ロシア・ソ連', '2014年のロシアによるクリミア併合。ウクライナ領を武力で奪取し国際的に非難');
INSERT INTO terms (id, name, tier, category, description) VALUES (583, 'BRICS', 3, '国際関係', '新興5か国の枠組み。ブラジル・ロシア・インド・中国・南アで2009年発足');
INSERT INTO terms (id, name, tier, category, description) VALUES (584, 'グローバルサウス', 3, '国際関係', '新興国・途上国の総称。グローバルサウスとして2020年代に国際的影響力増大');
INSERT INTO terms (id, name, tier, category, description) VALUES (585, 'DNA/分子生物学', 3, '現代', '遺伝情報の本体DNA。1953年ワトソンとクリックが二重らせん構造を発見');
INSERT INTO terms (id, name, tier, category, description) VALUES (586, '京都議定書', 3, '現代', '1997年の温室効果ガス削減議定書。先進国に削減義務を課した国際合意');
INSERT INTO terms (id, name, tier, category, description) VALUES (587, 'パリ協定', 3, '現代', '2015年の気候変動対策国際協定。パリ協定で全加盟国に削減目標を義務化');
INSERT INTO terms (id, name, tier, category, description) VALUES (588, 'ピカソ', 2, '現代', '20世紀最大の画家。キュビスムを創始しピカソは現代美術に革命をもたらした');
INSERT INTO terms (id, name, tier, category, description) VALUES (589, 'チャップリン', 3, '現代', '喜劇王と呼ばれた映画俳優。無声映画時代にチャップリンは社会風刺作品を制作');
INSERT INTO terms (id, name, tier, category, description) VALUES (590, 'アレクサンドリア', 3, '古代ギリシア', 'プトレマイオス朝の首都。ヘレニズム文化の中心で大図書館と灯台で有名');
INSERT INTO terms (id, name, tier, category, description) VALUES (591, 'エルサレム', 1, '中東', '三大宗教（ユダヤ教・キリスト教・イスラーム教）の聖地。パレスチナ問題の中心地');
INSERT INTO terms (id, name, tier, category, description) VALUES (592, 'アナーニ事件', 2, '中世ヨーロッパ', '1303年、フランス王フィリップ4世が教皇ボニファティウス8世を捕らえた事件。教皇権衰退の象徴');
INSERT INTO terms (id, name, tier, category, description) VALUES (593, 'ワルシャワ条約機構', 2, '冷戦', '1955年成立の東側諸国の軍事同盟。NATOに対抗して結成された');
INSERT INTO terms (id, name, tier, category, description) VALUES (594, '日独伊三国同盟', 2, '第二次世界大戦', '1940年締結。第二次世界大戦における枢軸国の軍事同盟');
INSERT INTO terms (id, name, tier, category, description) VALUES (595, 'カイロ宣言', 3, '第二次世界大戦', '1943年、米英中首脳が発表。日本の無条件降伏と領土処理を規定');
INSERT INTO terms (id, name, tier, category, description) VALUES (596, 'フセイン・マクマホン協定', 3, '第一次世界大戦', '1915-16年の英国とアラブの秘密書簡。アラブ独立を約束したが、後に矛盾が露呈');
INSERT INTO terms (id, name, tier, category, description) VALUES (597, 'ドルショック', 2, '冷戦', '1971年、ニクソン大統領がドルと金の交換停止を発表。ブレトンウッズ体制が崩壊した');
